INSERT INTO `zft_program` (`p_id`, `p_title`, `p_sort`, `p_tedian`, `p_jiancha`, `p_zhiliao`, `p_zz`, `p_link`, `p_att_id`, `p_score_type`, `p_age`, `p_sex`, `p_i_title`, `cas_pidsum`, `cas_sum`) VALUES
(1, 'Wilson`s 病', 1, '＃12～23歳が一番多い\r\n＃ALP低値(平均20)\r\n＃若者の血管内溶血＋肝疾患＋ALP低値→Wilson病\r\n＃肝臓CT＞70HU(正常値60)の疾患：ヘモクロマトーシス、Wilson病、糖尿病\r\n＃AST,ALT＜1500U、\r\n＃ALP/T-bil＜4.0, かつAST/ALT＞2.2→100％の特異度・感度でWilson病診断\r\n＃ALP低値(平均20)、非Wilson病での肝不全はALP平均140\r\n＃ALPが0になる唯一の疾患：Wilson病', '', '内服', '倦怠感、脱力、体調不良、浮腫、眼瞼腫脹、肢腫脹、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、貧血、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、肝脾腫、肝腫大、脾腫、筋肉痛、筋.{0,22}痛、アシドーシス、アシデミア、低K、低カリウム、血中K.{0,22}低、血清K.{0,22}低、血中カリウム.{0,22}低、血清カリウム.{0,22}低、汎血球減少、多飲、めまい、ふらつき、多尿、尿量増加、ALP.{0,22}上昇、ALP.{0,22}増加、ALP.{0,22}増多、ALP.{0,22}高値、胆道系酵素上昇.{0,22}上昇、胆道系酵素上昇.{0,22}増加、胆道系酵素上昇.{0,22}増多、胆道系', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHRzUyRVM5YlJNbUk" target="_blank">NASHとWilson病の鑑別</a>', 0, 0, 37.4, 0.46, '', 41, 1000),
(2, '悪性リンパ腫・白血病', 2, '＃リンパ節の腫大を主訴に来院することが多い\r\n＃発熱、寝汗、体重減少を生じる\r\n＃全身のリンパ節が腫大するのは、伝染性単核球症、HIV 感染、結核、SLE、関節リウマチ、血液悪性腫瘍、サルコイドーシス\r\n＃骨髄膨張→両下肢の放散痛→白血病の可能性\r\n＃あらゆる巨赤芽球貧血は骨髄内溶血性である\r\n＃脾腫があることは急性白血病を否定する\r\n＃原因不明の裂肛→血算→急性白血病の可能性\r\n＃好塩基球増多：CML→Ph染色体とBCR-ABLｍRNA検出\r\n＃慢性骨髄性白血病の血糖値0→偽物\r\n＃ 急性白血病がよく単/大関節痛を起こす(特に小児に多い)\r\n＃ 尿酸↑＋腎機能正常：悪性血液病\r\n＃末梢血に幼若な白血球、赤芽球の出現：癌の骨髄転移、骨髄線維症、重症感染症\r\n＃脾臓内多発性低吸収域＋ALP高値\r\n＃Neurolymphomatosis：悪性リンパ腫による末梢神経や神経叢，神経根への直接的な浸潤による病態を指し，放射線療法や. 化学療法による神経障害や傍腫瘍性症候群と区別される。 ', '＃骨髄への浸潤を伴わないかぎり、血算は正常であることが多い\r\n＃胸部/腹部 CT スキャンを施行する。縦隔リンパ節、腹腔内リンパ節の腫大を認める\r\n＃生化学検査では LDH の増加を認めることが多い。フェリチンの著増を認めることもある\r\n＃最終的にリンパ節の生検で診断する\r\n＃エコー：多結節状の形態を呈し、腫瘍内部を大血管が貫通する所見', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、頭痛、腹痛、腹部痛、心窩部痛、季肋部痛、胸水、 胸腹水、腹水、体液貯留、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、食欲不振、食思不振、食欲低下、食思低下、咳嗽、咳、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、けいれん、痙攣、肝脾腫、肝腫大、脾腫、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、SpO2低下、低酸素、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHQ3pYY3R4dkVxVTQ" target="_blank">メトトレキサート関連肝臓リンパ腫</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHdExrRVFxTTFhQTg" target="_blank">脊髄神経根症状(Neurolymphomatosis)を呈した胃悪性リンパ腫(DLBCL)の1例</a>', 0, 0, 66.8, 0.64, '', 85, 1000),
(3, '薬剤性', 3, '＃アミノレバンによる意識障害：アミノレバンにNa14,Cl94、頻繁に点滴すると高Cl性アシドーシス➡意識障害、高K血症\r\n＃意識障害の時は必ずガス分析を\r\nNSAIDS＋キノロン系→けいれん\r\nワーファリン＋キノロン/マクロライド/ST/NSAIDS→ワーファリン作用↑\r\n抗アレルギー薬＋マクロライド→QT↑\r\nテオフィリン＋マクロライド→テオフィリン中毒\r\nカルバペネム系＋バルプロ酸→ バルプロ酸濃度↓\r\nピルシカイニド:サンリズムの副作用：徐脈、QRS拡大→心室頻拍\r\n＃ベンゾジアゼピンと三環類併用時に、フルマゼニル使用するとベンゾを拮抗したが、三環類の副作用で痙攣を起こす可能性がある\r\n＃リスベリドン＋スルピリド(ドグマチール)の併用でドパミン遮断作用↑→誤嚥\r\n＃ワルファリン＋ロキソニンの併用でINR延長→腸腰筋血腫\r\n＃スルペラゾン、CMZの副作用：PT延長→出血傾向→VitK予防投与\r\n ＃ アセトアミノフェン中毒 (10 to 20 g over a period of 3 days)：アミラーゼは2000～10,000\r\n＃クラリス併用禁忌：偏頭痛治療薬の酒石酸エルゴタミン、、抗精神病薬のピモジド、睡眠剤のベルソムラ\r\n＃β遮断薬投与によるFEV1低下は10ml程度\r\n＃CTRXによる偽胆石症(CTRX使用後0日～2か月より発症)\r\n＃メトロニダゾール：小脳毒性がある\r\n＃βラクタム製剤によるの脳症：ミオクローヌス\r\n＃シグマートの副作用：眼瞼結膜潰瘍、肛門周囲潰瘍、結腸潰瘍\r\n＃アリセプトの副作用：ジストニア\r\n＃オルメテックなどARBによる腸粘膜絨毛萎縮→消化管吸収不良、腹部膨満感、下痢。\r\n＃偽性高Cl血症(ブロム中毒)→AG正常代謝性アシドーシス\r\n＃低γガンマグロブリン血症：カルバマゼピン\r\n＃シベンゾリン：腎機能低下している高齢者に抗コリン作用が亢進し、尿閉や筋力低下の副作用がある。', '', '＃薬物中毒の治療：脂肪乳剤点滴', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、皮疹、紅斑、紫斑、湿疹、丘疹、発疹、皮膚病変、皮膚障害、構音障害、呂律困難、呂律障害、失語、構語障害', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHNVlsMkpRYWVhd1U" target="_blank">小脳失調、構音障害を呈するメトロニダゾール脳症</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHaWk5YXJaZ2owQUE" target="_blank">抗生剤開始2日後に発症した薬疹症例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(4, '甲状腺機能亢進', 4, '＃倦怠感、発熱、頻脈の原因になる。興奮などの精神症状を生じることもある\r\n＃女性のほうが5-10倍頻度が高い。大部分は40-60歳で発症する\r\n＃甲状腺の腫大、腫瘤を触れる、触れないケースも認められるという\r\n＃心不全を合併することもある\r\n＃肺高血圧を生じる\r\n＃周期性四肢麻痺の原因になることもある\r\n＃シェーグレン症候群、糖尿病、副甲状腺機能低下症、重症筋無力症等を合併しうる\r\n＃潜在性甲状腺機能亢進症：TSH低下、FT4正常→AFや骨粗鬆症のリスク→専門医紹介\r\n＃甲状腺ホルモン不応症：FT4高値にもかかわらず、TSHが抑制されない状態\r\n＃爪甲剥離：乾癬性関節炎、甲状腺機能亢進症\r\n＃甲状腺腫瘍：石灰化、特に縦長の単一腫瘍内の多発点状石灰化を伴う時、悪性の確率が高い。\r\n＃甲状腺機能亢進→心房細動→腎梗塞発症', '＃TSHの減少とフリーT4、T3の上昇を見る\r\n＃骨代謝が更新し、ALPが上昇する。高Ca血症、低コレステロール血症、貧血、好中球の減少を生じる。赤沈が亢進する\r\n＃肝酵素の増多、ビリルビンの増多、フェリチンの増多を伴うこともある\r\n＃抗核抗体が高値であることが多い\r\n＃凝固能が亢進する。心房細動を合併した患者さんには抗凝固療法を行う\r\n＃甲状腺機能亢進症の診断がついた時点で甲状腺シンチを施行する。亜急性甲状腺炎や慢性甲状腺炎では取り込みの低下を見る\r\n☆甲状腺関連自己抗体\r\n＃TSHレセプター抗体が高値ならバセドウ病。\r\n＃抗マイクロゾーム抗体や抗サイログロブリン抗体といった抗甲状腺抗体が高値ならば慢性甲状腺炎の増悪を見ている可能性がある。\r\n＃抗甲状腺抗体は橋本病だけでなくバセドウ病でも上昇する。\r\n＃すべて陰性ならば亜急性甲状腺炎\r\n', '＃抗甲状腺薬：プロピルチオウラシル/PTUとメチマゾール/MMIがある\r\n＃PTUは甲状腺ホルモンの産生を抑えるだけでなく、末梢でのT4からT3への変換を抑える働きを持つ。このため重篤な甲状腺中毒ではPTUが第一選択となる\r\n＃MMIは半減期が長く、投与回数が少なくてすむため長期投与に向く\r\n＃どちらの薬剤も無顆粒球症の副作用があるため、発熱を生じたら血算を確認する\r\n＃PTUには不可逆的肝壊死の、MMIには胆汁うっ滞の合併症がごくまれに生じるので、定期的なフォローが必要\r\n＃妊娠した患者さんにはPTUを用いる\r\n＃ANCA関連血管炎をごくまれに合併する\r\n＃MPO-ANCAが陽性化する。副腎皮質ステロイドで治療する\r\n＃無機ヨード薬は甲状腺クリーゼや心不全合併症例など、速やかに甲状腺機能を低下させたいとき、副作用で抗甲状腺薬が使用できないときに適応になる。内服用ルゴール液を10-15滴/日内服するが、2週間程度で効果が減弱する\r\n☆甲状腺中毒の治療\r\n＃抗甲状腺薬→ステロイド→ヨード製剤の順で用いる\r\n＃PTUを初回400 mg、以降200 mg 1日3回投与\r\n＃プロプラノロール（インデラルR）を80-160mg/分4で開始する\r\n＃ヒドロコルチゾン100 mgを1日4回、またはデキサメタゾン2 mgを1日4回静注する。\r\n＃PTUの投与を行ってから用いるヨード製剤の経口を行う\r\n＃必ず1時間以上前に抗甲状腺剤を投与してからヨードの投与を行う\r\n＃発熱に対してアセトアミノフェンを用いる', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、頭痛、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、下痢、水様便、けいれん、痙攣、体重減少、体重低下、アシドーシス、アシデミア、低K、低カリウム、血中K.{0,22}低、血清K.{0,22}低、血中カリウム.{0,22}低、血清カリウム.{0,22}低、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム.{', '', 0, 0, 51.3, 0.36, NULL, 140, 1000),
(5, 'PBC', 5, '＃原発性胆汁性肝硬変（PBC）では、抗ミトコンドリア抗体（AMA）は95%の患者さんで陽性になるとされています。AMAの感度95%、特異度98%という報告（Clin Liver Dis. 2008;12(2):261.）もあり、PBCには特異的な抗体検査です。ただし、測定方法によって偽陽性・偽陰性があり、臨床的に疑われた場合にはELISA法で陰性でも間接蛍光抗体法で陽性になる患者が数％（逆もしかり）あるとされています。\r\n＃抗核抗体も60-70%で陽性になるとされていて、抗セントロメア抗体陽性のPBCは門脈圧亢進症を合併しやすいのだそうです。補助検査としてIgM上昇も参考になります。一般的にPBCの診断には肝生検による組織所見が重要ですが、特徴的な慢性非化膿性破壊性胆管炎（CNSDC）が存在しなくてもAMAが陽性で組織が矛盾しなければ診断可能となっています。\r\n＃診断基準\r\n(１)自覚症状\r\n皮膚掻痒感で初発することが多い。黄疸は出現後、消退することなく漸増することが多く、門脈圧亢進症状が高頻度に出現する。原発性胆汁性胆管炎（primary biliary chlangitis：PBC）（原発性胆汁性肝硬変）は臨床上、症候性（symptomatic）PBCと無症候性（asymptomatic）PBCに分類され、皮膚掻痒感、黄疸、食道胃静脈瘤、腹水、肝性脳症など肝障害に基づく自他覚症状を有する場合は、症候性PBCと呼ぶ。これらの症状を欠く場合は無症候性PBCと呼び、無症候のまま数年以上経過する場合がある。\r\n(２)血液・生化学検査所見\r\n症候性、無症候性を問わず、赤沈の亢進、血清中の胆道系酵素（アルカリホスファターゼ、γGTPなど）活性、総コレステロール値、IgM値の上昇を認め、抗ミトコンドリア抗体（antimitochondrial antibody：AMA）が高頻度に陽性である。\r\n(３)組織学的所見\r\n肝組織では中等大小葉間胆管ないし隔壁胆管に慢性非化膿性破壊性胆管炎（chronic non-suppurative destructive cholangitis：CNSDC）あるいは胆管消失を認める。連続切片による検索で診断率は向上する。\r\n(４)合併症\r\n高脂血症が持続する場合に皮膚黄色腫を伴う。シェーグレン症候群、関節リウマチ、慢性甲状腺炎などの自己免疫性疾患を合併することがある。\r\n(５)鑑別診断\r\n慢性薬物起因性肝内胆汁うっ滞、肝内型原発性硬化性胆管炎、成人性肝内胆管減少症など\r\n(６)診断\r\n次のいずれか１つに該当するものをPBCと診断する。\r\n①組織学的にCNSDCを認め、検査所見がPBCとして矛盾しないもの※\r\n②AMAが陽性で、組織学的にはCNSDCの所見を認めないが、PBCに矛盾しない（compatible）組織像を示すもの※\r\n③組織学的検索の機会はないが、AMAが陽性で、しかも臨床像（自覚症状、血液・生化学検査所見、合併症を総合したもの）及び経過からPBCと考えられるもの\r\n※検査所見がPBCとして矛盾しない：血液所見で慢性の胆汁うっ滞所見（ALP、γGTP）\r\n※PBCに矛盾しない組織像：胆管消失、肉芽腫など\r\n ', '', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、高血圧、血圧高値、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、頭痛、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、関節症状、関節痛、関節腫脹、関節炎、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、けいれん、痙攣、肝脾腫、肝腫大、脾腫、筋肉痛、筋.{0,22}痛、アシドーシス、アシデミア、低K、低カリウム、血中K.{0,22}低、血清K.{0,22}低、血中カリウム.{0,22}低、血清カリウム.{0,22}低、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム.{0,22}低、血清ナトリウ', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHVlM5akh5ajhOT2s" target="_blank">胆道系酵素のみならずASTとALTも著明に上昇したPBC症例</a＞\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHaXptSWJxZXMxWU0" target="_blank">ベザフィブラート有効のPBCの一例</a>', 0, 0, 59.6, 0.25, NULL, 140, 1000),
(7, 'PSC', 6, '', '', '', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、浮腫、眼瞼腫脹、肢腫脹、貧血、腹痛、腹部痛、心窩部痛、季肋部痛、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、下痢、水様便、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、汎血球減少、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、LD.{0,22}上昇、LD.{0,22}増加、LD.{0,22}増多、LD.{0,22}高値', '', 0, 0, 61.6, 0.56, NULL, 32, 1000),
(8, '胆嚢炎・胆管炎', 7, '＃典型的には食後に発症する右上腹部痛として発症する\r\n＃痛みは突然発症、徐々に落ち着いていくことが多い\r\n＃痛み、発熱、黄疸が Charcot の 3 徴と呼ばれる\r\n＃75% の患者さんに嘔吐を認める\r\n＃感染が波及すると肝膿瘍の原因になる\r\n＃尿路結石、狭心症、急性膵炎、肝炎、肝膿瘍、上部消化管潰瘍、腹部大動脈瘤、右下葉の肺炎を除外診断する必要がある\r\n＃Mirizzi症候群：胆嚢頸部結石が総胆管を圧迫する\r\n＃Lemmel症候群(レンメル)：傍乳頭憩室が胆管を圧迫する', '＃胆嚢有意な拡大＞8＊4cm\r\n＃腹部エコー、腹部 CT を施行する。検尿沈渣、血液生化学検査/AMY/Ca、PT/APTT、HBs 抗原、HCV 抗体を提出する\r\n＃感染を生じていた場合、胆のうが緊満しているにもかかわらず、胆のう壁は肥厚する。周囲への炎症の波及による dirty fat sign、周囲液体貯留を認める\r\n＃血清 GOT、GPT、LDH、ALP、Bil の上昇、白血球数の上昇を見る。AMYの上昇も頻度が高い\r\n＃白血球数 20000 以上、または総ビリルビン 10 以上は予後悪化の指標となる。穿刺の適応について外科に相談する\r\n＃50% 程度で血液培養が陽性になる\r\n＃外科的ドレナージが必要になる可能性があるので、PT/APTTを提出する\r\n＃壊疽性胆嚢炎CT所見：胆囊内腔あるいは壁内のガス像，胆囊内腔の膜様構造，胆囊壁の造影不良，胆囊周囲膿瘍。特異度低い所見(肝臓辺縁造影、胆嚢壁凹凸不整、胆嚢周囲液体貯留)', '＃絶食と補液を行う\r\n＃胆石の手術適応：糖尿病併存、充満胆石、大きな石、磁器様胆嚢\r\n＃アンピシリン/スルバクタム(ユナシン)3 g を 1 日 4 回、またはピペラシリン/タゾバクタム(ゾシン)4.5 g を 1 日 3 回、重篤な場合にはメロペネム(メロペン)1 g を 1 日 3 回使用する\r\n＃胆石の痛みには NSAIDs が効く。状態が安定していて、禁忌がないなら使用できる\r\n＃胆道の閉塞がなくても、1-4 mg/dl 程度のビリルビン上昇は珍しくない\r\n＃特に総胆管結石のときには、肝機能障害から PT 延長を生じうる。ビタミン K 5 mg の点滴投与に反応する\r\n＃痛みが取れるまでは原則禁食。手術適応の問題があるので、消化器外科の上級医に今後のことを相談する\r\n＃急性閉塞性化膿性胆管炎の場合には致命的な転機をとりうるので、ERCPによる内視鏡的胆道ドレナージを行う', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、腹痛、腹部痛、心窩部痛、季肋部痛、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、低血圧、ショック、血圧低下、食欲不振、食思不振、食欲低下、食思低下、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、消化管出血、吐血、下血、吐下血、血便、タール便、尿閉、脂肪織濃度上昇、脂肪', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHTExVNUFhMHdWM2M" target="_blank">胆嚢動脈発育</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHakEwbk96d2ttcEk" target="_blank">壊疽性胆嚢炎</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHcTdvZ2Jib2c0SkU" target="_blank">漏出性胆汁性腹膜炎</a>\r\n<a href="https://drive.google.com/open?id=1pHzTGL4BTmqcGZiodUycMs8309UmTtEW" target="_blank">顕微鏡的多発血管炎による胆嚢炎</a>', 0, 0, 70.1, 0.59, NULL, 56, 1000),
(9, 'アミロイドーシス', 8, '＃沈着疾患。関連疾患：痛風、偽痛風\r\n＃アミロイドーシスは原発(AL)、続発性(AA)、遺伝性に分けられる。\r\n＃AAアミロイド-シス：RA、結核に続発、9割がRA、RAの6％が発症。AAが血管壁中層に沈着、虚血になり、容易に出血や下血となる。顆粒状粘膜、小潰瘍、発赤、易出血性\r\n＃ALアミロイドーシスは特殊な形質細胞疾患で、免疫グロブリン軽鎖異常・増加。\r\n　　形質細胞関連疾患、骨髄腫に分類される\r\n　　SMT様隆起、粘膜下血腫、小潰瘍、発赤、易出血性、ALが血管外に沈着し、血管が脆弱になり、皮下血腫になりやすい\r\n　　生検：粘膜筋板や粘膜下層まで生検、十二指腸下降部9割の検出率に対し、大腸などは半分以下\r\n＃ALアミロイドーシスの特徴：原因不明の下痢、肝腫大、心筋症、末梢性ニューロパチー、ネフローゼ\r\n＃原因不明の心臓・肝臓肥大、尿たんぱく\r\n＃手根管症候群：甲状腺機能低下症、アミロイドーシス\r\n＃心不全＋腎不全：アミロイドーシス、Fabry病 \r\n＃腎障害：糸球体、間質、血管に沈着\r\n　　　　　糸球体に結節状に見える場合、糖尿病性糸球体硬化症と鑑別\r\n　　　　　PAM染色で糖尿病の結節は黒、アミロイドはピンク', '＃腹部画像検査では、蛋白沈着による腎腫大や腸間膜脂肪織炎をみとめる\r\n＃心エコー上、高輝度の心肥大を認めるにもかかわらず、心電図変化は低電位となる\r\n＃血清の蛋白分画でガンマグロブリンの単クローン性増加を認めた場合には、さらに免疫蛋白電気泳動を提出する\r\n＃電気泳動にて、血清 free light chain、尿中の Bence Jones 蛋白を認めればAL アミロイドーシスを、血清 CRP、SAA などが持続高値のときには AAアミロイドーシスをそれぞれ考える\r\n＃診断：胃十二指腸か直腸生検、組織への蛋白沈着を証明すること\r\n＃心電図：肥大型変化、左室容積減少型、陰性T、V1-2のQSパタン(偽性MIパタン)', '', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、浮腫、眼瞼腫脹、肢腫脹、高血圧、血圧高値、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、関節症状、関節痛、関節腫脹、関節炎、低血圧、ショック、血圧低下、食欲不振、食思不振、食欲低下、食思低下、下痢、水様便、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、ネフローゼ、SpO2低下、低酸素、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム.{0,22}低、血清ナトリウム.{0,22}低、胸痛、背部痛、胸部違和感、めまい、ふらつき、徐脈、失神、一過性意識障害、一過性意識.{0,22}低下、低Mg、低マグネシウム、血中Mg.{0,2', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHeXh0bEdvYXpnb2s" target="_blank">大腸限局性ALアミロイド-シスの一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHdzBSZmRDbDF4RFk" target="_blank">消化管アミロイドーシスの臨床像 </a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHWWY1TG4xXzBwRXc" target="_blank">非抗酸菌肺炎に合併した消化管アミロイド-シス</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHRllTRmpoNkpha0k" target="_blank">関節リウマチによる消化管アミロイドーシス</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHWWY1TG4xXzBwRXc" target="_blank">非抗酸菌肺炎に合併した難治性下痢・消化管アミロイド-シス</a>\r\n<a href="https://drive.google.com/open?id=1-O-PG4v8GXQtVfC9kE7O3fsDaI8McNRm" target="_blank">褥瘡で発症したAAアミロイドーシスの例</a>', 0, 0, 67.7, 0.53, NULL, 139, 1000),
(10, 'サルコイドーシス', 9, '＃肉芽腫性疾患。関連疾患：結核、非Langerhans細胞組織球症\r\n＃肺門部リンパ腫脹、多発性関節炎、結石性紅斑、ぶどう膜炎、触診リンパ節腫脹、高Ca、高UA、脾腫、ACE↑、中枢性尿崩症\r\n＃BALのリンパ球＞90％、CD4:CD8 ↑＞3.5（特異度 94% 感度52%）。しかし好中球＞2、好酸球＞1% の時、サルコイドーシスを否定する\r\n＃ 部分心筋運動低下：虚血性、サルコイドーシス and Chagas’ disease.\r\n＃ AVブロック＋心室内伝導障害：アミロイドーシス, sarcoidosis, Chagas病\r\n＃ アミロイド-シス心筋症：心筋肥厚、低QRS\r\n＃ FDG–PETとMRIが サルコイドーシス心筋症を評価できる\r\nACE増加：肉芽腫形成\r\n＃ ACE＜25 U :the likelihood ratio for sarcoidosis is 0.12; \r\n　ACE 25 ～71：  the likelihood ratio is 1.31;\r\n  ACE >71 U : the likelihood ratio increases to 7.15.\r\n＃全身症状を伴う中枢性尿崩症：サルコイドーシス、IgG4関連、悪性腫瘍\r\n＃脾臓低信号\r\n＃病因として、P. acnes (アクネ菌)感染の説あり、DNA検査ではほぼ100％検出される。PAB抗体免疫染色で確認できる。\r\n＃原因不明の全身性炎症性疾患。半数は肺間質影、両側肺門リンパ節腫脹で発見される。無症状の患者さんも多い\r\n＃全身症状として発熱、体重減少、疲労・倦怠感、呼吸困難、咳嗽を生じる\r\n＃肺結核、過敏性肺臓炎、悪性リンパ腫などが除外診断の対象になる\r\n■ ぶどう膜炎\r\n＃サルコイドーシスではぶどう膜炎を生じる。眼科依頼が必要になる\r\n＃ぶどう膜炎はサルコイドーシスのほか、Behçet 病、原田病、炎症性腸疾患、強直性脊椎炎などの全身疾患、ヘルペス感染症に合併する', '＃血液検査では白血球減少、赤沈の亢進、20% に高 Ca 血症を認める。40% に高 Ca 尿症を認める。蛋白分画でガンマグロブリンの増加を認める\r\n＃慢性炎症性疾患では、一般に血清 ACE は減少する。サルコイドーシスの患者さんは、40-80% の患者さんで血清 ACE が高値をとる\r\n＃全患者さんの 90% 以上に何らかの肺実質病変を生じる\r\n＃CT ではほぼ全例に肺門/縦隔リンパ節の腫大、小粒状の肉芽腫が散在する。上中肺野の内層優位分布、発熱や咳嗽などは呈しない。\r\n＃小粒状影は胸膜、小葉間隔壁、気管支血管束に沿う傾向があり、気管支血管束が結節状に腫大/肥厚して見える\r\n＃ガリウムシンチで肺門リンパ節の集積、皮膚や心筋の生検で非乾酪性類上皮肉芽腫を証明できれば診断が確定する\r\n＃気管支肺胞洗浄液のリンパ球数が増加する。CD4/CD8 細胞比が高くなる。疾患特異性は低い代わり、病勢を反映する\r\n＃KL-6 が上昇する。肺病変の機能予後を反映するという', '＃患者さんの 2/3 は数年以内に自然寛解、肺病変の軽快を見るので、無症状の患者さんは経過観察\r\n＃高 Ca 血症、関節炎、中枢神経障害、心サルコイドーシス、肝機能障害、進行する呼吸器症状を合併した患者さんについては、0.5-1.0 mg/kg/日からプレドニゾロンの内服を開始し、数ヶ月から数年かけて漸減していく\r\n＃治療とともに、血清中 ACE の低下を見る。ガリウムシンチや臨床症状/胸Xp などの反応は 2-4 週で得られる\r\n＃反応があったらプレドニゾロンを減量しつつ、最低でも 1 年続ける\r\n＃TNF 阻害薬のインフリキシマブ(レミケード)に肺外病変に対する治療効果が期待されている', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、倦怠感、脱力、体調不良、高血圧、血圧高値、頭痛、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、咳嗽、咳、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、体重減少、体重低下、肝脾腫、肝腫大、脾腫、筋肉痛、筋.{0,22}痛、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、SpO2低下、低酸素、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、低Ca、低カルシウム、血中Ca.{0,22}低、血清Ca.{0,22}低、血中カルシウム.{0,22}低、血清カルシウム.{0,22}低、高Ca、高カルシウム、血中Ca.{0,22}高、血中カルシウム.{0,22}', '<a href="https://drive.google.com/open?id=1BYLhKeP2sytxG2WV9z7nbGfGO_bKBEms" target="_blank">サルコイドーシスの疾病発生メカニズム</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHQXBvTUIwelA2Z2M" target="_blank">食道に限局したサルコイドーシスの一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHS3hOMVJXNV9wTHc" target="_blank">結節型筋肉サルコイドーシスの一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHRDd4bHpQVFRxa0E" target="_blank">腹膜サルコイドーシスの一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHNHBIOFlEMkpMdVk" target="_blank">神経サルコイドーシス</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHZG8yeDB6c1hBaEE" target="_blank">肺サルコイドーシスUpdate</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHZ3JVakpNdXNTNnc" target="_blank">尿崩症を呈するサルコイドーシス</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHM05ubm9fMVdkbXc" target="_blank">心臓サルコイドーシス</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHNHBIOFlEMkpMdVk" target="_blank">半身麻痺と嗅覚幻覚を呈した中枢サルコイドーシス</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHZG8yeDB6c1hBaEE" target="_blank">嫌気性菌P.acnesによる肺サルコイドーシスの一例</a>\r\n<a href="https://drive.google.com/open?id=1Tr3_89wrF52WJz43vlM-V-8apKlTonsM" target="_blank">高Ca伴わない腎機能障害を呈したサルコイドーシス</a>\r\n<a href="https://drive.google.com/open?id=1aetYouZTU3wt5plqr6am2RLhfBFOpfq7" target="_blank">無症状の肺サルコイドーシスの1例</a>', 0, 0, 71.3, 0.48, '肝臓サルコイドーシス', 69, 1000),
(11, '多発性骨髄腫', 10, '＃形質細胞疾患、平均で 65 歳程度の高齢者に多い。40 歳以下には少ない\r\n＃レイノー現象や網状皮疹を生じることもある\r\n＃ベンスジョーンズ蛋白を伴う多発性骨髄腫が低グロブリン血症を起こす\r\n＃IgA型多発性骨髄腫が髄外性腫瘍を作りやすく、縦隔腫瘍の鑑別診断にあげられる\r\n＃IgM型多発性骨髄腫は高Ca血症を起こす可能性が低い\r\n＃多発性骨髄腫3つのNo→発熱なし、ALP↑なし、脾腫なし\r\n＃IgG、IgM、IgAが同時に上昇→多クローン性→腫瘍(多発性骨髄腫など)ではなく、膠原病や慢性炎症の可能性あり\r\n＃多発性骨髄腫の約30%では、重鎖と軽鎖（IgGカッパ型など）が血液中に見られると同時に、尿中にも軽鎖（カッパ型軽鎖など、BJP）が認められます。\r\n＃MMの10％がBJP型多発性骨髄腫で、BJP（軽鎖の二量体、M蛋白の一種）だけ産生する。分子量が小さいのですぐに尿中に排泄され、血液中濃度が低いため、M蛋白として認識されない。免疫電気泳動試験のM-bowを作れないが、尿の蛋白分画や免疫電気泳動で確認できる。\r\n＃血清蛋白分画検査上M蛋白様バンドを確認できたら、免疫電気泳動を実施し、M蛋白の存在と種類を確認し、尿の蛋白分画や免疫電気泳動で、BJPの存在も確認する。\r\n＃BJPが存在すれば、予後が悪くなる。\r\n＃M蛋白が出現する疾患：多発性骨髄腫、MGUS、H鎖病、原発性マクログロブリン血症などがある。\r\n＃原因不明のCRABO(Ca↑、腎不全、貧血、骨痛、アミロイドーシス、過粘稠、易感染)を見たとき、MMを疑う\r\n＃多発性骨髄腫を疑う時に提出する検査：IgG、IgM、IgAのどれか突出し他は低下\r\n　　すべて減少する場合：Bence Jone型かIgD型を疑う\r\n＃MMをさらに検査する時血清·24H尿蛋白電気泳動(PEP)＋血清·24H尿免疫電気泳動(IEP)＋血清遊離軽鎖(sFLC)をセットで提出\r\n＃PEP＋IEP＋sFLC検査でM蛋白を発見したが、IgG、IgA、IgMに突出や低下を認めず、臓器障害もない場合→MGUS\r\n＃M蛋白がIgMの場合、B細胞がクラススイッチを経ていない、すなわち形質細胞まで分化していないことを意味するため、悪性リンパ腫やCLLなどB細胞腫瘍を疑う\r\n＃MMによる腎障害：骨髄腫腎、アミロイドーシス\r\n＃稀にアミロイド沈着による横紋筋肥大→筋肉質を呈する', '＃TPとALBの解離、尿蛋白の定性と定量の解離(BJP陽性の場合)を認めることがある。\r\n＃正球性貧血を 8 割に認める。好中球減少、血小板減少はまれ。\r\n＃蛋白尿、高 Ca 血症を生じる。赤沈の著明な亢進を見る。\r\n＃蛋白分画で単クローン性のガンマグロブリン増加(M ピーク)を認める。\r\n＃Bence-Jones 蛋白は容易に尿中排泄されるため、血清蛋白にピークが認められない場合には尿の電気泳動を行うことで確認できる。\r\n＃総蛋白増加、アルブミンの減少を認める。総コレステロールは低下していることが多い。\r\n＃高 Ca 血症による腎機能障害を認める。\r\n＃痛みを訴える部位の単純写真を撮影することで、骨融解像を確認する。', '', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、倦怠感、脱力、体調不良、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、貧血、関節症状、関節痛、関節腫脹、関節炎、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、低血圧、ショック、血圧低下、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、けいれん、痙攣、筋肉痛、筋.{0,22}痛、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、SpO2低下、低酸素、胸痛、背部痛、胸部違和感、高Ca、高カルシウム、血中Ca.{0,22}高、血中カルシウム.{0,22}高、、血清Ca.{0,22}高、血清カルシウム.{0,22}高、汎血球減少、咽頭痛、LD.{0,22}上昇、LD.{0,22}増加、LD.{0,22}増多、', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHNjJPSUI0MUwzZTQ" target="_blank">多発性骨髄腫を合併したC型肝炎の1例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHTlRGZnVRczJtenM" target="_blank">多発性骨髄腫による心臓ALアミロイドーシス</a>', 0, 0, 71.2, 0.59, NULL, 140, 1000),
(12, '結核・播種性非結核性抗酸菌感染症', 11, '＃BCG既接種者、結核菌が感染後、XPに陰影が出るまで4ヵ月、T-SPOTは2ヵ月\r\n＃すなわち濃厚接触者は、接触後2～3か月後にT-SPOT検査すべき\r\n#肺MAC症との鑑別：壁が厚い空洞、気管支拡張所見なし、中葉・舌区に所見なし、胸水あり、多葉に病変なし\r\n＃空洞を有する結節影の鑑別：結核、MAC、肺クリプトコッカス症\r\n＃慢性的に経過する発熱、全身倦怠、咳嗽、夜間の発汗などで来院する\r\n＃呼吸困難を訴える患者さんは比較的少ない\r\n＃特に高齢者の再発例の場合、診断がつきにくいことがある\r\n＃胸水ADA＞70、かつリンパ球/好中球＞0.75\r\n＃結核培養：小川培地(4～8週)、MGIT(2週間)\r\n＃ADA＞60で結核性胸膜炎を診断、感度95％、特異度96％。実はSLE胸膜炎や、膿胸·溶血を伴う癌性胸膜炎でも上昇\r\n＃結核で単球増加(単球/リンパ球＞0.8)は予後不良を示唆する\r\n＃結核性腹膜炎の診断：腹水ADA＞40\r\n＃結核性心外膜炎の診断：心嚢液ADA＞40、リンパ球/好中球＞1、インターフェロンγ＞50、末梢白血球＜10000\r\n＃抗酸菌・結核性腸炎：盲腸・上行結腸中心の輪状潰瘍、瘢痕、炎症性ポリープ(UC,CrohnなどIBDもみとめる)', '＃クォンティフェロン による結核検査は、感度 89%、特異度 98%。BCG の影響を受けない\r\n＃胸部単純写真では上肺野を中心とした浸潤影、空洞形成をみる。胸水を伴うこともある\r\n＃抗酸菌の塗沫標本は迅速であるものの、抗酸菌と非結核性抗酸菌との鑑別はできない\r\n＃喀痰培養が困難であった場合には、胃液の培養を提出する\r\n＃ガフキー号数 3 号以上の患者さんはハイリスクの感染として対処する\r\n＃若者の日本人がQTF陽性＝結核感染を意味する\r\n＃特徴CT所見：胸膜面に達するレベルに至るまで、非常に辺縁が明瞭な高いコントラストの微細分岐影を呈する\r\n＃糖尿病合併肺結核は、肺門部と下肺野に好発、浸潤が多くコンソリデーションを呈する。空洞もよく見られる\r\n＃IL-6高値でCRPも上昇する\r\n＃原因不明の腹水貯留でCA-125が上昇している場合, 卵巣癌が思い浮かぶと思いますが, 結核も合わせて想起したいところ.\r\n＃活動性結核と血清CA-125値には相関性があり, 治療により低下する．特に結核性漿膜炎や, 肺結核でも喀痰抗酸菌染色陽性, 空洞性病変, 広範囲の病変ではCA-125は高値となりやすい\r\n＃結核ではCA-125が1000U/mLを超える場合もあることはあるが, 基本的に3桁程度までと考えるべき. 4桁行く場合はやはり癌を考えた方が良いかもしれない.', '＃結核治療歴がないQTF陽性の患者に対して、1日あたりプレドニン15mg以上を使用する場合には、イソニアジドによるLTBIの治療を6～9か月を行う\r\n＃結核治療歴があるQTF陽性の患者に対してはイソニアジド投与の適応外。 \r\n■ 入院までの流れ\r\n＃排菌を生じた患者さんは、基本的に全員入院の適応。ガフキー陽性の結核患者さんであっても、ガフキー 3 号以下、あるいは抗結核薬投与後 2 週間たった患者さんは、感染の危険が下がるといわれている\r\n＃入院患者さんは基本的に個室隔離、本人には外科用のマスクをつけてもらい、他の人は N95 マスクを着用する\r\n＃ガフキー 4 号以上の排菌者は原則として結核専門病院へ転送\r\n＃医療従事者の感染頻度は高い。感染者が入院した場合はとりあえずは介護者は 35 歳以上の人に限定、それ以下の人は入職時のツ反陽性であることを確認してから介護業務に入ってもらう\r\n■ 結核の発生届\r\n＃結核は法律で届け出が義務づけられた伝染病であるので、結核と診断した場合、ただちに所轄保健所に結核発生届を行う\r\n＃結核患者さん以外に、無症状の原体保有者で治療が必要な人、疑似症患者さんも届け出対象\r\n＃2007 年 4 月、感染症法の改正が行われ、結核予防法は感染症法に統合され\r\nた。従来の結核予防法 35 条は感染症法の第 37 条に、34 条は同第 37 条の2 に移行した\r\n＃患者さんを隔離して入院治療を行う場合は感染症法第 7 条の 2 を申請\r\n＃外来で通院治療する場合は感染症法第 37 条を申請\r\n＃費用の公費負担については従来どおり、37 条の 2 が申請されれば結核医療基準の 95/100 が公費負担され、37 条が申請されれば医療保険の患者負担分について公費負担が行われる\r\n■ 院内で結核が発生した場合\r\n＃従来は、患者さんのガフキー号数 × 咳の期間が 10 以上であった場合、広\r\n範な結核伝播の可能性があるとされていた\r\n＃感染症法への統合以降、化学療法前の 3 回の喀痰検査で、1 回でも塗沫陽性かつ同定検査で結核菌群陽性の場合には高感染性とされ、さらに喀痰塗沫検査が陰性であっても、画像所見から肺結核と判断される有空洞例も高感染性と判定されることになった\r\n＃高感染性と判定された患者さんに対して、接触した職員、家族をピックアップし、患者さん発見から 2 ヶ月後にツ反を行う\r\n＃この時点でツ反の陽性化がなかった職員は、その 1 年後と 2 年後にそれぞれツ反、胸 Xp を施行。寝汗や体重減少といった症状の有無を確認する\r\n＃ツ反については、入職時のツ反がある職員ではそのときより 20 mm 以上大きくなっていたら陽性、そうでない職員では発赤の大きさが 30 mm 以上あったら感染の危険ありと判断する\r\n＃現在は、6 歳以上で高感染性の患者さんと接触した接触者検診では、QFTをツ反に代えて行うことが望ましいとされている\r\n＃感染の可能性ありとされた職員は、INH 7-8 mg/kg を 9 ヶ月間、予防的に内服してもらう\r\n■ 抗結核薬について\r\n＃初発した結核に対して、イソニアジド(イスコチン)、リファンピシン(リマクタン)、ピラジナミド(ピラマイド)、エタンブトール(エブトール)による 4 剤併用療法を 2 ヶ月行い、その後 INHFP による 2 剤併用療法を 4 ヶ月継続する\r\n＃通常イソニアジド 0.3 g/日、リファンピシン 0.45 g/日、ピラジナミド1.5 g/日、エタンブトール 0.75 g/日程度を用いる\r\n＃イソニアジドには肝炎、皮疹、末梢神経障害の副作用があるため、ビタミン B6\r\n＃リファンピシンはステロイドやワーファリン、抗けいれん薬の効果を減弱させる。', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、胸水、 胸腹水、腹水、体液貯留、関節症状、関節痛、関節腫脹、関節炎、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、食欲不振、食思不振、食欲低下、食思低下、咳嗽、咳、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、けいれん、痙攣、体重減少、体重低下、肝脾腫、肝腫大、脾腫、筋肉痛、筋.{0,22}痛、SpO2低下、低酸素、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHYkx2cU0xdnF3bE0" target="_blank">播種性非結核性抗酸菌感染症の1例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHQlNjYk5TeWFUOFk" target="_blank">腸結核</a>\r\n<a href="https://drive.google.com/open?id=1un-kNvEoTtsoOXrhJGraxpKvR8BqzLQR" target="_blank">結核性腹膜炎</a>\r\n<a href="https://drive.google.com/open?id=1aDOz-4U0pBy_GmzDVSSEaGVrKJeocOtW" target="_blank">全身リンパ節腫脹、炎症反応高値を呈し、Castleman病と誤診した播種性非結核性抗酸菌症の1例</a>\r\n<a href="https://drive.google.com/open?id=1I8x2kJe7szSbPLzMLRo61WC0y7GBouvW" target="_blank">NEJMの腸結核1例</a>', 0, 0, 62.3, 0.70, '腎結核と尿管結核のCT画像', 76, 1000),
(13, 'ANCA関連血管炎', 12, '＃CHCC 2012分類：\r\n　　大型血管炎(大動脈と四肢や頭頚部に向かう最大の分枝)：高安動脈炎、巨細胞性動脈炎\r\n　　中型血管炎(各臓器に向かう主要動脈)：結節性多発性動脈炎PN、川崎病\r\n　　小型血管炎：ANCA関連(MPA、GPA(Wegener)、EGPA(Churg-Strauss)、免疫複合体血管炎(抗糸球体基底膜病、クリオグロブリン血症性血管炎、IgA血管炎、低補体性蕁麻疹様血管炎・抗C1q血管炎)\r\n　　多彩な血管を侵す血管炎：Behcet、Cogan症候群\r\n＃大動脈炎：Behcet病, 再発性軟骨炎, 高安病、巨細胞炎、梅毒、サルモネラ', '', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、浮腫、眼瞼腫脹、肢腫脹、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、貧血、腹痛、腹部痛、心窩部痛、季肋部痛、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、血小板.{0,22}増多、血小板.{0,22}上昇、血小板数.{0,22}上昇、血小板数.{0,22}増多、血小板.{0,22}増加、血小板数.{0,22}増加、フェリチン.{0,22}上昇、フェリチン.{0,22}増加、フェリチン.{0,22}増多、フェリチン.{0,22}高値、消化管出血、吐血、下血、吐下血、血便、タール便、結膜.{0,22}充血、結膜炎、結膜.{0,22}発赤、ALP.{0,22}上昇、ALP.{0,22}増加、ALP.{0,22}増多、ALP.{0,22}高値、胆道系酵素上昇.{0,22}上昇、胆道系酵素上昇.{0,22}増加、胆道系酵素', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHTFY1amZPNkRIbDQ" target="_blank">＃多発肝動脈瘤を伴ったANCA血管炎</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHX2RwcldxYk5HX0k" target="_blank">＃ANCA血管炎に合併した肺ノカルジア症</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHaTJkOWhKUTNpc28" target="_blank">＃動脈瘤破裂による胆道出血を来したANCA関連血管炎</a>\r\n', 0, 0, 0.0, NULL, NULL, 0, 1000),
(14, '悪性腫瘍', 13, '＃ALP正常でも原発性や転移性骨腫瘍は除外できない。\r\n＃ALP正常の場合、骨シンチも異常なし。\r\n＃トルーソー（Trousseau）症候群：悪性腫瘍に伴う血液凝固亢進→血栓性静脈炎、梗塞\r\n＃傍腫瘍性神経症候群（paraneoplastic neurological syndrome PNS）：担癌患者に合併する神経障害のうち，免疫学的機序により生じると考えられる多様な症候群である．\r\n＃腫瘍熱：プロカルシトニン・PCT陰性、G-CSF(WBC↑)やIL-6(CRP↑)の上昇\r\n　　ナプロキセン1回100～200mgを1日3回投与、速やかに解熱', '＃免疫染色：CK7＋：肺、前立腺、乳がん\r\n　　CK20＋：大腸や胆管など\r\n　　CK7-、CK20-：消化管由来ではない\r\n＃Desmin陰性：平滑筋腫を否定する\r\n＃S-100蛋白陰性：神経鞘腫を否定する\r\n＃腎がん： BFP\r\n＃肺がん：CYFRA 、 SCC、ProGRP、NSE 、 CEA 、CA19-9\r\n＃卵巣がん ：CA125\r\n＃子宮がん ： SCC、CA125\r\n＃乳がん：CA15-3、CEA 、HER2\r\n＃甲状腺がん：カルシトニン、サイログロブリン、CEA\r\n＃ 骨転移：ICTP、 TRACP-5b、NTX、BAP\r\n\r\n', '', 'ALP高値\r\n下痢\r\nCPR高値\r\n胸水\r\n腹水\r\n腎機能障害・腎不全\r\nリンパ節腫大\r\nフェリチン>561\r\n腸間膜脂肪識濃度上昇\r\n消化管出血\r\n肝腫大\r\n血中Na低下\r\n腹痛\r\n頸部・咽頭痛\r\nVEGF高値\r\n末梢神経障害\r\n脾腫大\r\nsIL-2R高値\r\n結節性紅斑\r\n高ガンマグロブリン血症\r\n四肢痛\r\nSAAG＜1.1g/dl \r\n好塩基球増多\r\n咳嗽\r\n血痰\r\nびまん性肺変化\r\n浮腫\r\n便秘\r\n血中Na高値\r\n血中Ca高値\r\n血小板増多\r\n腰痛\r\n単球増加\r\n貧血\r\n呼吸困難\r\n喘鳴\r\n視力障害', '', 0, 0, 0.0, NULL, NULL, 0, 1000),
(15, '副甲状腺機能亢進', 14, '＃高 Ca 血症を生じる。P は低下または正常下限をとる\r\n＃骨痛や腎結石を生じる。無症状の患者さんも多い\r\n＃iPTH が高値となる\r\n＃除外診断のため、PTHrP とビタミン D 濃度を提出する\r\n＃甲状腺エコーや核医学検査で診断する。手術の適応となる\r\n＃副甲状腺機能亢進症の家族歴を認めた場合には、褐色細胞腫と甲状腺癌の検索を行う\r\n＃血中Ca＜13.5mEq/dL、便秘、多尿', '', '', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、倦怠感、脱力、体調不良、高血圧、血圧高値、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、頭痛、腹痛、腹部痛、心窩部痛、季肋部痛、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、関節症状、関節痛、関節腫脹、関節炎、食欲不振、食思不振、食欲低下、食思低下、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、筋肉痛、筋.{0,22}痛、低K、低カリウム、血中K.{0,22}低、血清K.{0,22}低、血中カリウム.{0,22}低、血清カリウム.{0,22}低、胸痛、背部痛、胸部違和感、低Ca、低カルシウム、血中Ca.{0,22}低、血清Ca.{0,22}低、血中カルシウム.{0,22}低、血清カル', '', 0, 0, 64.0, 0.29, NULL, 82, 1000),
(16, '梅毒', 15, '＃無痛性陰部もしくは口腔内潰瘍→梅毒をまず除外\r\n＃手掌、足底皮疹→まず2期梅毒、IEを除外\r\n＃手掌の発疹＋黄疸：梅毒\r\n＃梅毒性肝炎：胆道系酵素優位の肝障害、皮疹、発熱、門脈域にリンパ球浸潤\r\n＃二次性梅毒：前房膿瘍、視力低下、霧視、歩行困難、聴力低下、手掌皮疹', '#胆管系酵素高値、ALPなど', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、頭痛、腹痛、腹部痛、心窩部痛、季肋部痛、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、けいれん、痙攣、ネフローゼ、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、咽頭痛、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、めまい、ふらつき、補体.{0,22}低下、補体.{0,22}減少、低補体、動脈瘤破裂、末梢神経障害、しびれ、痺れ、異常知覚、感覚異常、感覚鈍麻、知覚障害、単球.{0,22}増加、単球.{0,22}増多、単核球.{0,22}上昇、単核球.{0,22}増多、単核球.{0,22}増', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHbnd5Q2pSMmhRNG8" target="_blank">梅毒性肝炎の一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHbmVPN292UTAwREk" target="_blank">胆道系酵素優位の肝障害を呈する梅毒性肝炎</a>\r\n<a href="https://drive.google.com/file/d/0B0XnmO-afdkHYmRwS1BhWDgyTjQ/view" target="_blank">神経障害を呈する二次性梅毒の一例</a>\r\n<a href="https://drive.google.com/open?id=1vsJoAD91zYseBKcVv2x8DxRUXcOddROu" target="_blank">内科学会雑誌の梅毒性肝炎の1例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(17, 'シェーグレン症候群', 16, '＃シェーグレンの 腺外病変（全身性臓器病変）：発熱、高ガンマグロブリン血症性紫斑病、多発神経炎などの血管炎、肺、腎、肝、神経、筋などの活動性病変、無菌性髄膜炎、横断性脊髄炎など\r\n＃髄液検査にて細菌性髄膜炎様な異常を呈する場合がある\r\n＃自己免疫性疾患では, 他にSLEやRAなどでもリンパ腫は合併するが, pSSが最も合併リスクが高い.\r\n＃一般人口と比較すると7-19倍のリスク\r\n＃多いのは唾液腺のMALT(mucosa associated lymphoid tissue)リンパ腫で, 一般人口の1000倍のリスクとなる\r\n＃pSSにおけるNHL合併リスク因子を抽出: 唾液腺の腫脹, リンパ節腫大, レイノー現象, 抗SSA, SSB抗体陽性, RF陽性, M蛋白, C4低下がリスク因子となる各項目を1ptとすると,　≤2ptならば合併率は3.8%　3-6ptならば39.9%, OR 16.6[6.5-42.5]　7ptならば100%, OR 210.0[10.0-4412.9]\r\n＃口渇＋下肢の脱力：シェーグレン、Lambert–Eaton myasthenic syndrome', '', '', '倦怠感、脱力、体調不良、貧血、関節症状、関節痛、関節腫脹、関節炎、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、食欲不振、食思不振、食欲低下、食思低下、咳嗽、咳、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、体重減少、体重低下、ネフローゼ、筋肉痛、筋.{0,22}痛、アシドーシス、アシデミア、低K、低カリウム、血中K.{0,22}低、血清K.{0,22}低、血中カリウム.{0,22}低、血清カリウム.{0,22}低、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、胸痛、背部痛、胸部違和感、汎血球減少、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、CK.{0,22}上昇、CK.{0,22}増加、CK.{0,22}増多、CK.{0,22}高値、CPK.{0,22}上昇、CPK.{0,22}増加、CPK.{0,22}増多、CPK.{0,22}高値、多飲、補体.{0,22}低下、補体.{0,22}減少、低補体、多尿、', '<a href="https://drive.google.com/open?id=1owUU_sdLHd4MfhT32kCB96NxnmMCiXbY" target="_blank">シェーグレン症候群review</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(18, '関節リウマチ', 17, '＃初発症状として関節痛、関節炎、朝のこわばりを生じる。複数の関節の痛みを生じ、たいていは左右対称に病変を生じる\r\n＃特に手関節に症状が出やすい。関節炎は骨の変形や破壊を伴う\r\n＃40-50 代女性、60-80 代男性に主に認められる\r\n＃頸椎病変を生じうる。慢性期に環軸椎亜脱臼を生じ、四肢麻痺を生じることがある\r\n＃胸椎、腰椎の病変は極めてまれ\r\n＃RA患者の長引く咳：MAC症、気道病変(気管支拡張→LVFX、クラリス)\r\n＃若者の関節炎はまずSLEを除外してから、RAを考える\r\n　　　中高齢者はまずSjogrenを除外し、RAを考慮\r\n＃PIP、MPと手根骨全体が好発、まれにDIPも変形\r\n＃DIP関節病変：変形性関節炎、乾癬性関節炎、シェーグレン、まれに関節リウマチも\r\n＃回帰性リウマチ：20～30歳代に好発、移動性関節炎、1回の発作期間は数時間～数日と短く、間欠期は数日～数か月。発作は手指関節、膝、足関節などの単関節炎を認める。約半数が抗CCP抗体やRF抗体陽性、有症期間が短いため、関節破壊は生じない', '＃赤沈と CRP は亢進する。白血球数は上昇しないか、むしろ減少していることがある。リンパ球増加を見る。血小板数は上昇している\r\n＃リウマチ因子が 85% の患者さんで陽性\r\n＃抗 CCP 抗体は 80-90% の患者さんで陽性。リウマチ因子よりも特異度が高いため、他の関節炎との鑑別に有用\r\n＃SLE に比べて頻度は低いが、抗核抗体も陽性になる患者さんが多い\r\n＃リウマチ性関節炎に好中球減少と脾腫を合併したものを Felty 症候群と呼ぶ\r\n＃単純写真上の関節の変型所見は特異性が高い', '＃関節の痛みに対して NSAIDs を用いる\r\n＃ステロイド投与の前に、メトトレキサート、スルファサラジンの内服といった抗リウマチ薬の治療が優先される\r\n＃5-10 mg のプレドニゾロン内服は、症状の緩和に効果がある\r\n＃TNF 阻害薬をはじめとする生物学的製剤の適応がある。', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、腹痛、腹部痛、心窩部痛、季肋部痛、関節症状、関節痛、関節腫脹、関節炎、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、食欲不振、食思不振、食欲低下、食思低下、咳嗽、咳、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、肝脾腫、肝腫大、脾腫、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム.{0,22}低、血', '', 0, 0, 0.0, NULL, 'メトトレキサート関連皮下結節', 0, 1000),
(19, 'ガストリノーマ', 18, '＃ガストリノーマはガストリン産生腫瘍で，通常膵臓または十二指腸壁に発生する。結果として胃酸の過剰分泌と進行の速い難治性の消化性潰瘍が生じる（Zollinger-Ellison症候群）。\r\n＃診断は血清ガストリン濃度の測定による。\r\n＃ガストリノーマは，膵島細胞から発生する膵内分泌腫瘍の一種であるが，ガストリノーマの80～90％は膵臓または十二指腸壁に発生する。残りは脾門，腸間膜，胃，リンパ節，または卵巣に発生する。\r\n＃約50％の患者では複数の腫瘍がみられる。ガストリノーマは通常，小さく（直径1cm未満），増殖は緩徐である。\r\n＃約50％は悪性である。\r\n＃ガストリノーマ患者の約40～60％に多発性内分泌腫瘍症が認められる（ 多発性内分泌腫瘍 （MEN）症候群）。\r\n', '', '', '意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、腹痛、腹部痛、心窩部痛、季肋部痛、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、下痢、水様便、体重減少、体重低下、高Ca、高カルシウム、血中Ca.{0,22}高、血中カルシウム.{0,22}高、、血清Ca.{0,22}高、血清カルシウム.{0,22}高、膨満感、腹部違和感、心窩部不快感、高P、高リン、血中P.{0,22}高、血清P.{0,22}高、血中リン.{0,22}高、血清リン.{0,22}高、低蛋白、低アルブミン、口渇', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHQmU1UVJyc1c1V0U" target="_blank">下痢が誘発され急性腎不全を発症した十二指腸ガストリノーマ症例</a>\r\n<a href="https://drive.google.com/open?id=1jNbEUTSShY5awGItyrR0xz6B2R1KGUVD" target="_blank">慢性下痢を呈した肝臓ガストリノーマによるZollinger-Ellison症候群の一例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(20, '髄膜炎', 19, '＃発熱を伴ったひどい頭痛で来院する\r\n＃頸部が硬かったら診断に役立つものの、項部硬直を伴わない人も多い\r\n＃いやいやをするように頭を振ってもらうことで頭痛が強くなれば髄膜炎の可能性は高く、痛くならなければ髄膜炎はほぼ否定できる\r\n＃原因不明の髄膜炎では起因菌としてアメーバも鑑別に挙げれる\r\n＃クリプトコッカス髄膜炎の95％は血清クリプトコッカス抗原陽性。ほぼ全例髄液検査で初圧が亢進する。\r\n＃ヘルペス脳炎の診断：HSV-DNA、抗体\r\n＃補体C5～9欠損者は侵襲性髄膜炎菌感染症のリスクが高い', '＃髄液中細胞数の増加を確認する。細菌性髄膜炎では好中球が増加する。リンパ球が増加するのはウィルス性髄膜炎、脳膿瘍に伴うリンパ球増多、SLE、結核 、真菌感染、癌性髄膜炎、ADEM、サルコイドーシス、血管炎 .\r\n＃髄液中白血球数が 2000 以上、糖 20 以下、蛋白 200 以上であった場合には細菌性髄膜炎である可能性が高い \r\n＃細菌性髄膜炎の場合、末梢血の白血球数は増加していることが多い \r\n＃細菌性髄膜炎では赤沈の亢進、CRP の増加を認める\r\n＃ウィルス性髄膜炎は、髄膜炎全体の 7.5% と決して高頻度ではない\r\n＃結核性髄膜炎は 2 週-3 ヶ月の亜急性発症で来院する。神経麻痺を生じることもある。髄液検査では軽度の圧亢進と中程度の細胞数増加、蛋白の増加、糖の低下を生じる 。髄液中の ADA が増加する 。髄液 で診断する\r\n＃CT では異常を認めない。造影 MRI 検査を行うと、T1 強調像で脳表や脳底槽、脳溝に一致した増強効果が認められる \r\n＃脳髄液検査：グラム染色、抗酸菌、真菌染色、クリプトコッカス抗原、CMV、HSV-1/2、VZV、EBV、HHV-6、ウエストナイルウイルス、トキソプラズマ、Lyme病、リンパ脈絡髄膜炎ウイルス、HTLV', '＃セフトリアキソン(ロセフィン) 2 g を 1 日 2 回点滴静注、またはメロペネム(メロペン) 2 g を 1 日 3 回点滴静注、さらにバンコマイシン15 mg/kg 1 日 3 回点滴静注を併用する\r\n＃抗生剤に加えてデキサメタゾンを 0.15 mg/kg を 6 時間ごとに静注する。2-4 日間併用する \r\n＃HSV-2 型による髄膜炎、HSV-1 型による脳炎 .を疑った際には抗生物質に加えてアシクロビル(ゾビラックス)10 mg/kg を 8 時間ごとに点滴静注する\r\n＃高齢者、妊婦、アルコール依存や糖尿病を合併した髄膜炎に対して、リステリアをカバーするためにアンピシリン 2.0 g を 4 時間ごとに併用する \r\n＃抗生剤の投与期間は、インフルエンザ桿菌ならば 7 日間、髄膜炎菌ならば3-7 日間、肺炎球菌なら 10-14 日間、グラム陰性菌なら 21 日間\r\n＃日本のガイドラインで「CRP 陰性化まで抗生剤を続けること」と記述しているものがある。一定期間で抗生剤を中止してもいいのかどうか、必ず上級医と相談して決めるべきだと思う\r\n＃髄液から髄膜炎菌が培養された場合、患者さん周囲で接触のあった人にリファンピシン 600 mg を 12 時間ごとに 4 日間、またはシプロフロキサシン500 mg を 1 回内服してもらう \r\n＃髄液からインフルエンザ桿菌が培養された場合、家庭内接触者に対してリファンピシン 600 mg を 24 時間ごとに 4 日間予防内服してもらう \r\n＃髄膜炎の治療期間：肺炎球菌10～14日間\r\n　　髄膜炎菌、インフルエンザ桿菌7日間\r\n　　B群溶連菌：14～21日間\r\n　　リステリア：21日間以上\r\n＃細菌性髄膜炎：糖＜45、血糖の2/3、蛋白>500㎎/dl、WBC＞1000\r\n＃細菌性髄膜炎の治療：VCM15㎎/㎏＊3回＋CTRX2g＊2回＋プレドニン1g＊1＋ガンマグロブリン5g＋免疫不全ありアンピシリン4g＊2回＋アシクロビル10㎎/㎏＊3回\r\n＃細菌性髄膜炎の患者の意識改善あれば、髄液検査再検査は不要\r\nMSSA髄膜炎：セファゾリンの組織移行度が低いため、使いにくい。\r\n　　CTRXなどはMSSAにイマイチ。\r\n　　セフェピムかメロペンが第一選択となる。\r\n＃髄膜炎治療：免疫不全なし：CTRX＋VCM\r\n　　免疫不全あり：VCMーーMRSA\r\n　　　　マキシピームーー緑膿菌\r\n　　　　ビクシリンーーLisetria\r\n＃Campylobacter fetus：メロペン', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、頭痛、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、関節症状、関節痛、関節腫脹、関節炎、食欲不振、食思不振、食欲低下、食思低下、けいれん、痙攣、筋肉痛、筋.{0,22}痛、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、腰痛、めまい、ふらつき、膨満感、腹部違和感、心窩部不快感、頚部痛、単球.{0,22}増加、単球.{0,22}増多、単核', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHU3hWZ1NjUGxPNkE" target="_blank">EBV再活性による無菌性髄膜炎に伴う尿閉症例</a>\r\n<a href="https://drive.google.com/open?id=1QtrahTlY0mxx6uXc609zdwgOmiNgX5r8" target="_blank">補体欠損による再発性髄膜炎菌性髄膜炎</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(21, 'TAFRO症候群', 20, '＃血小板減少、胸水・腹水、腎障害、骨髄線維化、リンパ節軽度腫大＜1.5cm、IL-6上昇→CRP↑\r\n＃急性或いは亜急性に発熱や全身浮腫、胸水、腹水及び血小板減少を来す全身炎症性疾患。腎障害、貧血、臓器腫大(肝脾腫、リンパ節腫大)など呈する\r\n＃体液貯留が目立ち、Castleman病と似たデータ＋血小板減少＋経過が早い\r\n＃IgG上昇やM蛋白がない\r\n＃発熱、血小板減少、低LDH血症、高ALP、胸腹水', '', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、倦怠感、脱力、体調不良、浮腫、眼瞼腫脹、肢腫脹、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、貧血、腹痛、腹部痛、心窩部痛、季肋部痛、胸水、 胸腹水、腹水、体液貯留、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、食欲不振、食思不振、食欲低下、食思低下、咳嗽、咳、肝脾腫、肝腫大、脾腫、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、汎血球減少、抗核抗体.{0,22}陽性', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHQ2ZqSjlTcHhGdzA" target="_blank">TAFRO症候群の一例</a>\r\n<a href="https://drive.google.com/open?id=1_jPsEwIk2czrskQQoOyYX9V-0cft4FCo" target="_blank">神戸大学のTAFRO1例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(22, 'SLE', 21, '＃不明熱、全身倦怠感、体重減少を生じる。皮膚の発赤疹、関節痛、レイノー現象を伴っている患者さんも多い\r\n＃胸膜炎、胸水、気管支炎で発症することも多い。糸球体腎炎の合併も多い\r\n＃85% は女性に発症する。20-40 歳の若い人に多い\r\n＃精神症状、けいれん発作を生じることもある。抗リン脂質抗体の関与による動静脈血栓が原因になっていることが多い\r\n＃腸間膜動脈の虚血を生じることがあり、結節性多発動脈炎との鑑別が問題になる\r\n＃T細胞疾患で、特にCD4＋のT細胞減少と機能低下が注目される。\r\n＃口腔内潰瘍は自覚症状が大事：硬口蓋の無痛性潰瘍はSLEの特徴\r\n＃抗核抗体陰性でほぼ否定する病気：SLE、MCTD、自己免疫性肝炎\r\n＃抗核抗体陽性でほぼ否定する疾患：Still病、Behcet病\r\n＃SLEの嚥下痛：血管炎？\r\n＃Raynaud現象は膠原病の経過中に、MCTDの99％、強皮症で7～80％、SLEの18～44％、筋炎の10％以下に認める。', '＃白血球減少、リンパ球減少、血小板減少、貧血、補体の低下を見る\r\n＃糸球体腎炎を合併した場合には血尿、蛋白尿を認める\r\n＃赤沈高値だが、CRP の上昇は乏しいことが多い\r\n＃抗核抗体が 95% の患者さんで陽性。抗 DNA 抗体の感度は低いが、特異度は高い\r\n＃抗 DNA 抗体が陽性であった場合には、抗 sm 抗体、抗 RNP 抗体、抗カルジオリピン抗体、ループスアンチコアグラントを提出する\r\n＃抗リン脂質抗体が陽性になっていることもあり、APTT 延長の有無を確認する', '＃関節症状に対して NSAIDs の内服が処方される\r\n＃腎機能障害や胸水貯留、溶血性貧血、心膜炎、中心系の合併症、血小板減少を認めた際にはステロイド投与の適応\r\n＃プレドニゾロン 40-60 mg の分割経口投与から開始する。中枢神経症状を合併しているケースではより高用量が必要になることがある\r\n＃腎機能障害に対してシクロフォスファミドの投与が行われる\r\n＃ステロイド抵抗性で腎炎や中枢神経症状を合併した症例に対して血漿交換が行われる\r\n＃抗リン脂質抗体による凝固異常を合併していた場合には、ワーファリンやヘパリンによる抗凝固療法が必要', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、浮腫、眼瞼腫脹、肢腫脹、貧血、腹痛、腹部痛、心窩部痛、季肋部痛、胸水、 胸腹水、腹水、体液貯留、関節症状、関節痛、関節腫脹、関節炎、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、下痢、水様便、咳嗽、咳、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、ネフローゼ、筋肉痛、筋.{0,22}痛、胸痛、背部痛、胸部違和感、汎血球減少、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、LD.{0,22}上昇、LD.{0,22}増加、LD.{0,22}増多、LD.{0,22}高値、補体.{0,22}低下、補体.{0,22}減少、低補体、頻脈、フェリチン.{0,22}上昇、フェリチン.{0,22}増加、フェリチ', '<a href="https://drive.google.com/open?id=1r_TyfTYINZjSCyw_iHxNKPQPh1CYRMg-" target="_blank">SLE関連脳症頭・蓋内圧亢進の一例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(23, 'HUS・TTP', 22, '＃血小板減少、溶血性貧血、発熱、腎機能障害、精神症状を呈する\r\n＃特発性 TTP では血漿 ADAMTS13 活性の減少が、HUS では大腸菌 O157または O145 の関与が知られている\r\n＃感染や膠原病、薬物に合併する。クロピドグレル、チクロピジンによるものが有名\r\n＃ハプトグロビンの著減を見ることで、ITP との鑑別ができる\r\n＃血栓性微小血管障害症(TMA)はTTPやHUS, atypical HUS, 高血圧緊急症など。このうちTTPはADAMTS13活性の低下が認められ, 治療には血漿交換が重要.\r\n＃ADAMTS13活性は検査で評価できるものの, 時間がかかるため, この活性低下をどう予測するかが重要.\r\n＃ADAMTS13活性が低下している患者群(TTP)の方が血小板が低値で腎障害は軽度.\r\n＃ADAMTS13活性が低下していない例(aHUS)では血小板は若干高く, 腎障害は高度\r\n＃血栓性微小血管障害症におけるADAMTS13活性低下の予測: PLASMICスコア\r\n　　血小板 <3万/µL\r\n　　溶血所見(網赤血球>2.5% or ハプトグロビン検出濃度以下 or 間接ビリルビン>2.0mg/dL)\r\n　　活動性悪性腫瘍無し\r\n　　臓器移植, 幹細胞移植歴なし\r\n　　MCV<90fl\r\n　　INR<1.5\r\n　　Cr<2.0mg/dL　をそれぞれ1pt\r\n＃スコアとADAMTS13活性低下の可能性：0-4はLow risk　0-4%；5はIntermediate　5-24%；6-7はHigh risk群　62-82%でADAMTS13活性の低下の可能性あり\r\n＃血栓性微小血管障害症(TMA)：＃TTPを疑う時に提出する検査：ADAMTS13活性、AMTS13インヒビター、vWF活性、Combs試験陰性、破砕赤血球＋\r\n　　TMAを合併したループス腎炎(link)\r\n　　TTP、HUS、aHUS(C3のみ低下)、膠原病の血管炎、腫瘍など\r\n＃発見のきっかけ：貧血、LDH上昇、血小板低下→ハプトグロビン低下、直接Coombs試験陰性、網状赤血球上昇→溶血性貧血→破砕赤血球→ADAMTS13活性、AMTS13インヒビターを提出→速やかに血漿交換を', '＃HUS が疑われた際には便培養、O157LPS 抗体を提出する\r\n＃TTP において、CRP は普通上昇しない\r\n＃LDH が著増する。1000 以上に上昇することは珍しくない。間接ビリルビンの増加を認める\r\n＃PT/APTT、FDP は DIC で高値。HUS/TTP では通常正常値をとる\r\n＃貧血を生じていることが多い。網状赤血球数が増加する。末梢血スメアにて破砕赤血球を確認できる\r\n＃血小板数は通常 6 万以下に減少する\r\n＃尿検査にて蛋白尿、顕微鏡的血尿を生じる', '＃血漿交換と FFP の輸注を行う。血漿 40-60 ml/kg/日を診断後 3-5 日間は連日交換、血小板数が回復したら 2-3 日に 1 回ずつ施行し離脱する\r\n＃溶血性尿毒症症候群の患者さんであっても、中枢神経症状を伴う際には血漿交換の適応とされる\r\n＃血漿交換に併用する形でプレドニゾロン 1 mg/kg の内服/静注を行う\r\n＃ジピリダモールを補助的に用いる\r\n＃血小板輸血は血漿交換を行うまでは禁忌', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、浮腫、眼瞼腫脹、肢腫脹、高血圧、血圧高値、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、貧血、腹痛、腹部痛、心窩部痛、季肋部痛、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、胸水、 胸腹水、腹水、体液貯留、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、低血圧、ショック、血圧低下、下痢、水様便、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、けいれん、痙攣、LD.{0,22}上昇、LD.{0,22}増加、LD.{0,22}増多、LD.{0,22}高値、血小板.{0,22}増多、血小板.{0,22}上', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHeHFRTjB0enhfMVE" target="_blank">大腸菌によるHUSと脳症</a>\r\n<a href="https://drive.google.com/open?id=1iPHYsTJVBJhgzeo3cvPkmEJG64y6jRy_" target="_blank">破砕赤血球を呈さない3年無症状の再発性TTPの1例</a>\r\n<a href="https://drive.google.com/open?id=1r6eYVXyVXPvB7oVOzzzwv26yVaI8SMHA" target="_blank">非典型HUSの一例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(24, '貧血', 23, '＃VitB12、葉酸欠乏の診断：ホモシスチン上昇\r\n＃服薬中や、巨赤芽球性貧血がない場合はホモシスチン上昇が唯一の指標になる\r\n＃広義の腎性貧血：EPO低値、ESA低反応(尿毒性など)、CKDによる赤血球寿命短縮、透析時の回路内残血\r\n＃ESAの使用頻度：HD患者(目標はHb10～11)はweekly、CKD保存期(目標はHb11～13)はmonthlyでもOK\r\n＃CKD保存期のESA選択：ミルセラ、ネプス；初期使用量25～30μg/2週\r\n＃ESA使用のHb上昇速度、週Hb0.5以下、月Hb2以下が安全。早すぎる欠点：血栓リスク、血圧上昇\r\n＃鉄分必要量＝⊿Hb＊120\r\n＃鉄分補充量＝鉄分必要量＋貯蔵量(500mg)\r\n＃鉄分過剰補充しない目安：フェリチン＜100ng/mL\r\n＃小球性貧血の時に： RDW正常→ サラセミア\r\n　　　　　　　　　   RDW高 →バラツキ大きい→鉄欠乏\r\n＃徹底的精査にもかかわらず原因不明の貧血：MDS\r\n＃慢性疾患の貧血：Hb>9g\r\n＃Cr>1.5、Ccr<30、腎性貧血の可能性ある。特に糖尿病がある場合は早期に出現する。\r\n＃MCV＞130、巨赤芽球貧血\r\n　MCV＜120、骨髄疾患(MDS)、肝疾患\r\n＃維持透析者のEPOは10台、Hb7.7＋－1.5\r\n＃ 網赤血球率The RPI is the reticulocyte count × [the patient’s hematocrit ÷ a normal hematocrit] × [1 ÷ the red-cell maturation time, in days]; in this case the RPI is 1.7 × [35 ÷ 45] × [1 ÷ 1.5], or 0.9.) The normal RPI range is 1.0 to 2.0. Values above 2.0 suggest an adequate marrow response, whereas values below 1.0 (as in this case) indicate an inadequate marrow response.', '＃MCVが120以上の貧血時、まずビタミンB12欠乏を疑う\r\n＃ハプトグロビン：ビリルビン上がるほどの微量溶血ではなくでも感度以下に低下', '＃胃切後のビタミンB12欠乏、1年ごと1コースの貧血改善までの経口補充\r\n＃鉄分補充：鉄剤＋ビタミンC', '倦怠感、脱力、体調不良、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、頭痛、汎血球減少', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHQ1dSd1A0bGczYkU" target="_blank">貧血の鑑別診断</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHc0c0MWpydDNuRzQ" target="_blank">Hair-on-End Sign</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHb0JGRWNvT3ZycFk" target="_blank">TTP様症状を呈した葉酸欠乏症</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(25, 'Castleman病', 24, '＃貧血(Hb4、6)で、血清蛋白・総蛋白が10g/dlまで異常高値、IgG5000以上もなり得る。\r\n＃ALP上昇をみるがLDHが上がらない\r\n＃発熱、腎機能障害、リンパ節腫脹\r\n＃IL-6上昇のため、CRPも高値\r\n＃形質細胞関連疾患のPOEMS、アミロイド－シスと類似、M蛋白が検出できない場合もある\r\n＃多クロー ン性高免疫グロブリン血症を伴う原発性形質細胞性リン パ節症（idiopathic plasmacytic lymphadenopathy with polyclonal hyperimmunoglobulinemia，IPL）とも言える\r\n＃胸部CT：縦隔・腋窩リンパ節腫脹や、肺野の小葉中心性粒状影、すりガラス影や嚢胞形成をみとめることがある。', '', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、倦怠感、脱力、体調不良、浮腫、眼瞼腫脹、肢腫脹、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、貧血、腹痛、腹部痛、心窩部痛、季肋部痛、胸水、 胸腹水、腹水、体液貯留、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、体重減少、体重低下、肝脾腫、肝腫大、脾腫、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、汎血球減少、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗', '', 0, 0, 0.0, NULL, NULL, 0, 1000),
(26, '収縮性心膜炎', 25, '＃心機能正常、腎機能正常の、原因不明の浮腫と腹水、呼吸困難で来院する\r\n＃心膜の肥厚や心嚢水を伴わないケースがあり、疑わないと診断できない\r\n＃頻脈、肝腫大を生じる。肝不全に伴う浮腫や原因不明の浮腫としてフォローされていることがある\r\n＃結核などの慢性炎症によるもの以外に、乳癌、肺癌、リンパ腫、リウマチ性関節炎、SLEに合併する\r\n■心膜炎\r\n＃上気道炎症状のあとに発生した胸痛と呼吸苦を主訴に来院する\r\n＃症状は徐々に発症する。比較的典型的な狭心痛を呈する\r\n＃痛みは体動や仰臥位で増悪する\r\n＃聴診で心膜摩擦音を聴取できれば、診断価値は高い\r\n＃ウィルス/細菌感染、結核、SLEA、強皮症等の膠原病、リウマチ熱、血管炎、成人 Still 病、甲状腺機能低下症、心筋梗塞後、腎不全、リンパ腫などの悪性腫瘍に合併する\r\n＃前胸部の痛みを生じる疾患として、心筋梗塞、胆のう炎や急性膵炎、気胸や解離性大動脈瘤を除外診断する必要がある', '＃胸部 CT 上、心膜が 4 mm 以上に肥厚していることが多いが正常な場合もある。心膜の石灰化を探す\r\n＃BNP は症状の割に上昇せず、200 pg/ml 程度までのことが多い\r\n＃心エコーをオーダーしても「正常」であることが多い。肺高血圧を合併しない。下大静脈は拡張している\r\n＃両室カテーテル検査で左右心室の拡張期圧が同じことが証明できれば確定診断\r\n＃心室圧曲線では、特徴的な dip and plateau パターンを生じる\r\n■心膜炎\r\n＃心電図、血液生化学検査/AMY、赤血球沈降速度、CRP、ASO、CPK、TSH/FT4、RF、抗核抗体、抗 DNA 抗体、フェリチンを提出する。ANCA、クォンティフェロンを提出する。心エコー、胸部 CT スキャンで心嚢水の有無を確認する\r\n＃心電図では全誘導の ST 上昇を生じる。ST 低下を生じる誘導が存在しないのが特徴\r\n＃心エコー上左室の動きは正常。心嚢水を伴い、タンポナーデを生じることもある\r\n＃トロポニンは 35-50% のケースで上昇している\r\n＃心嚢穿刺を行った際には、細胞数、糖、総蛋白、LDH、pH、細胞診、一般培養、抗酸菌培養、ADA、Tb-PCR を提出する\r\n＃心エコーは 24 時間後、7 日目、30 日目、3 ヶ月後、6 ヶ月後、1 年後にそれぞれ確認する必要がある', '＃外科的な心膜切除が第一選択\r\n＃利尿薬による治療を行う。血圧が急激に下がる可能性があるためごく少量より使用する\r\n■心膜炎\r\n＃通常は保存的治療のみ。胸痛に対しては NSAIDsを使用する\r\n＃難治例ではプレドニゾロンの内服を行うこともある\r\n＃心嚢水の貯留を生じた場合は、穿刺可能であればドレナージをはかる\r\n■ 結核性心膜炎\r\n＃心嚢水の培養や の感度は低く、確定診断が難しい\r\n＃胸部 CT スキャンで縦隔リンパ節の腫大を伴うことが多い\r\n＃滲出性の心嚢水を生じる。リンパ球優位の細胞増多を生じる\r\n＃心嚢水 ADA が増加する。35 U/L をカットオフとすることで、感度 90%、特異度 74% であったという\r\n＃発熱、夜間の発汗を伴うことが多い。白血球数は 1000 以下であることが多い\r\n＃4 剤併用による標準的な結核治療を行う。収縮性心膜炎予防のため、ステロイドの併用が推奨されている', '浮腫、眼瞼腫脹、肢腫脹、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、胸水、 胸腹水、腹水、体液貯留、低血圧、ショック、血圧低下、咳嗽、咳、肝脾腫、肝腫大、脾腫、胸痛、背部痛、胸部違和感、頻脈、徐脈、失神、一過性意識障害、一過性意識.{0,22}低下、膨満感、腹部違和感、心窩部不快感、血痰、乏尿、無尿、単球.{0,22}増加、単球.{0,22}増多、単核球.{0,22}上昇、単核球.{0,22}増多、単核球.{0,22}増加、単球.{0,22}上昇、高K、高カリウム、血中K.{0,22}高、血清K.{0,22}高、血中カリウム.{0,22}高、血清カリウム.{0,22}高、低蛋白、低アルブミン、肺.{0,22}陰影、肺.{0,22}浸潤、肺.{0,22}網状、肺.{0,22}すりガラス、肺.{0,22}斑状、肺.{0,22}粒状、肺.{0,22}結節', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHajFaZFhCVzZZX3c" target="_blank">原因不明の胸水を呈する拘束性心外膜炎の一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHMHRVTXBwZVJEcjg" target="_blank">原因不明の腹水を呈する拘束性心外膜炎の一例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(27, '門脈塞栓', 26, '＃担癌患者、突然の腹水↑、肝不全、腹痛は軽度\r\n＃門脈内HCT低下、単純CTで門脈がLOWになる\r\n＃高アンモニア血症まれの原因：門脈閉塞による門脈体循環短絡\r\n＃高アンモニア血症：肝硬変、門脈大静脈シャント、ウレアーゼ産生菌、室温で検体を放置、バルプロ酸ナトリウムの副作用', '＃胆管系で造影CTで血栓にて閉塞した門脈枝が拡張胆管のようにみえる。要注意', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、高血圧、血圧高値、貧血、腹痛、腹部痛、心窩部痛、季肋部痛、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、胸水、 胸腹水、腹水、体液貯留、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、下痢、水様便、体重減少、体重低下、SpO2低下、低酸素、高Ca、高カルシウム、血中Ca.{0,22}高、血中カルシウム.{0,22}高、、血清Ca.{0,22}高、血清カルシウム.{0,22}高、CK.{0,22}上昇、CK.{0,22}増加、CK.{0,22}増多、CK.{0,22}高値、CPK.{0,22}上昇、CPK.{0,22}増加、CPK.{0,22}増多、CPK.{0,22}高値、多尿、尿量増加、白血球.{0,22}減少、白血球数.{0,22}減少、白血球.{0,22}軽度減少、膨満感、腹部違和感、心窩部不快感、消化', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHQ1dSd1A0bGczYkU" target="_blank">特発性門脈閉塞症</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(28, '甲状腺機能低下', 27, '＃寒気やだるさ、non-pitting edema、嗄声、眼瞼浮腫などを生じて来院する\r\n＃40-50代の女性に発症しやすい。男性に比べて女性に5倍多い\r\n＃C型肝炎 の患者さんに合併することも多い\r\n＃高脂血症や不妊、流産などを訴える患者さんは、一度はTSH/FT4、抗甲状腺抗体を測定しておく\r\n＃神経症状として、軽度の脱力、運動の緩慢化、手根管症候群を生じる。甲状腺ホルモンの補充により回復することが多い\r\n＃このときには抗生剤とG-CSFを使用するとともに無機ヨードの内服に切り替える\r\n＃MMIは乳汁中に分泌されるため、妊娠中はどちらでもいいらしい\r\n＃末梢でのT4からT3への変換抑制を期待してPTUを使う。\r\n＃MMIでも臨床的にはそう変わらないらしい\r\n＃急性期には遮断薬の効果が抑制されてしまうので高用量で開始、コントロールがついたら減量するルゴール液1回10滴6時間ごと7日間\r\n＃サリチル酸は甲状腺ホルモンの遊離を促してしまう可能性がある\r\n＃リンパ浮腫に類似した見た目になる\r\n＃I型糖尿病   副腎不全 、自己免疫疾患の合併や家族歴を見ることがある\r\n＃うつ病と診断されることがある。性格変化や精神症状、意識障害の原因にもなる\r\n＃ネフローゼ症候群、心不全、アミロイドーシス等を除外診断する必要がある\r\n＃チラージン内服中の方が、ネフローゼを合併時、尿中蛋白漏出によるFT4低下を起こす可能性がある\r\n＃まれの原因：海藻類過剰摂取→ヨード過剰摂取\r\n＃CK高値', '＃正-大球性貧血、GOT/GPT/LDH/CPKの高値、高脂血症。低Na血症を生じることもある。赤沈が亢進する\r\n＃慢性甲状腺炎が主な原因となる。抗マイクロゾーム抗体（抗TPO抗体）、抗サイログロブリン抗体を提出する。抗核抗体が陽性になることも多い\r\n＃拡張期高血圧を生じる。\r\n＃心電図では徐脈と低電位、QT延長を見る\r\n＃胸部画像所見では胸水を伴っていることが多い。\r\n＃心嚢水を認めることもある', '＃甲状腺機能低下症の症状が出現している場合、甲状腺の結節を伴う場合、高脂血症を伴う場合、TSHが10μ/ml以上に上昇している場合に治療の適応になる\r\n#チラージンの内服を行う。TSHの正常化を目標に', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、倦怠感、脱力、体調不良、浮腫、眼瞼腫脹、肢腫脹、高血圧、血圧高値、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、胸水、 胸腹水、腹水、体液貯留、食欲不振、食思不振、食欲低下、食思低下、けいれん、痙攣、SpO2低下、低酸素、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム.{0,22}低、血清ナトリウム.{0,22}低、低Ca、低カルシウム、血中Ca.{0,22}低、血清Ca.{0,22}低、血中カルシウム.{0,22}低、血清カルシウム.{0,22}低、汎血球減少、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、CK.{0,22}上昇、CK.{0,22}増加、CK.{0,', '', 0, 0, 0.0, NULL, NULL, 0, 1000),
(29, 'IE', 28, '＃全身倦怠感、熱源不明の発熱で来院する\r\n＃腰背部痛や関節痛、筋肉痛を訴えることもある\r\n＃溶血性連鎖球菌による亜急性心内膜炎が多い\r\n＃黄色ブドウ球菌による急性心内膜炎は致命率が高い\r\n＃亜急性感染性心内膜炎で39°Cを超えることはめったにない。急性心内膜炎は39-40°Cの高熱が出現する', '＃可能なら経食道心エコーを行し、疣贅の有無を確認する\r\n＃血液培養を複数回行うことで、9 割以上のケースで血培陽性になる\r\n＃白血球数が増加する。CRP、赤沈が亢進する。リウマチ因子が半数で陽性\r\n＃赤血球沈降速度が正常な場合は、感染性心内膜炎はほぼ否定できる\r\n＃結核とともに、単球の増加をみる\r\n＃補体の低下をみる\r\n＃血尿、蛋白尿を合併することも多い\r\n＃まれにIgG、κ、λ、漿細胞増多の原因になる', '＃15 分以上あけて 3 回の血液培養を施行する\r\n＃バンコマイシン 15 mg/kg の 1 日 2 回静注にアンピシリン(ビクシリン)2 g を 4 時間ごと静注、さらにゲンタマイシン(ゲンタシン)1 mg/kg を 1 日 3 回併用する\r\n＃血液培養の結果を待って抗生剤を変更する\r\n＃弁輪への膿瘍形成が生じると脚ブロックになる。心電図モニター下に経過を観察する\r\n＃塞栓を飛ばしてしまう可能性が高まるため、抗凝固薬、抗血小板薬を用いてはならない\r\n＃ゲンタマイシンのピークは 3 μg/ml、トラフ値は 1 μg/ml 以下が望ましい\r\n＃血液培養で S. bovis が検出された際には、落ち着いたら大腸癌や大腸ポリープを検索する必要がある。大腸カメラをオーダーする\r\n■ 手術の適応\r\n＃抗生剤無効の感染性心内膜炎\r\n＃2 回以上全身の塞栓症状を生じた場合\r\n＃重篤な弁機能不全を合併した場合\r\n＃真菌による感染性心内膜炎\r\n＃人工弁に感染した IE\r\n＃入院後に脚ブロックが出現した場合\r\n■ 予防的抗菌薬投与が必要な場合\r\n＃歯科処置\r\n＃扁桃摘出\r\n＃食道硬化療法/狭窄拡大術\r\n＃ERCP/胆道系の手術\r\n＃消化管の手術\r\n＃膀胱鏡施行時\r\n＃前立腺/尿道の手術時\r\n＃血液培養結果前：VCM15～20㎎/㎏＊2＋ゲンタマイシン3㎎/㎏＊　1回＋人工弁リファンピシン\r\n＃治療期間：血液培養陰性を1日目とし、自然弁は4～6日間、人工弁は6週間以上。だた連鎖球菌のゲンタマイシン使用が2週間\r\n＃脳梗塞を合併時ヘパリンは禁忌', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、貧血、低血圧、ショック、血圧低下、食欲不振、食思不振、食欲低下、食思低下、体重減少、体重低下、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、胸痛、背部痛、胸部違和感、腰痛、補体.{0,22}低下、補体.{0,22}減少、低補体、動脈瘤破裂、喘鳴、wheezing、結膜.{0,22}充血、結膜炎、結膜.{0,22}発赤、脂肪織濃度上昇、脂肪織炎、脂肪織混濁、構音障害、呂律困難、呂律障害、失語、構語障害、好中球減', '', 0, 0, 0.0, NULL, NULL, 0, 1000),
(30, 'クリオグロブリン血症', 29, '＃基礎疾患がない患者さんにクリオグロブリンが誘因となって生じる血管炎のことをクリオグロブリン血症性血管炎という。2012年に米国ノースカロライナ州で開催された血管炎の命名に関する国際Chapel Hillコンセンサス会議では、免疫複合体の関与する小血管（主に毛細血管、細静脈、細動脈）の血管炎に分類されました。\r\n＃10万に一人と比較的稀な疾患です。男女比はやや女性に多く,50～60歳代に好発します。\r\n＃クリオグロブリンは3つのタイプに分類されます。I型：単クローン性免疫グロブリン（10～15%）； II型：多クローン性IgGと単クローン性IgM（50～60%）； III型：多クローン性IgGと多クローン性IgM（30～40%） \r\n＃クリオグロブリンが血中に増加した状態をクリオグロブリン血症と呼び、II型とIII型のことを混合型クリオグロブリン血症という。以前は基礎疾患のないものを特に本態性クリオグロブリン血症と呼んでた。しかし、1989年にC型肝炎ウイルスが同定され大半の症例にC型肝炎ウイルス感染症が関与していることがわかった。\r\n＃I型クリオグロブリンを生じる疾患として多発性骨髄腫やマクログロブリン血症など血液疾患。\r\n＃II・III型クリオグロブリンを生じる疾患としてSjögren症候群や全身性エリテマトーデスなどの膠原病、悪性リンパ腫などのリンパ増殖性疾患、各種感染症が多い。\r\n＃全身倦怠感、下腿の「触知しうる」紫斑、網状皮疹、関節痛を生じる\r\n＃半数の患者さんに蛋白尿を伴う糸球体腎炎を生じる\r\n＃レイノー現象の原因になる\r\n＃血管炎の一つに分類される\r\n＃肝脾腫、肝機能障害を生じる。末梢神経障害を生じることもある\r\n＃95% は C 型肝炎に合併する。悪性リンパ腫や多発性骨髄腫、EB ウィルスや CMV 感染、関節リウマチ、SLE、強皮症、シェーグレン症候群に合併することもある\r\n＃検査室から血算が凝固した→クリオグロブリン血症\r\n＃HCV関連クリオグロブリン血症： C4減少\r\n＃40％のHCV患者がクリオグロブリン血症をもっているが、2～3％が血管炎を起こしている。その血管炎の中80％が圧痛性紫斑、50％が神経障害、30％が膜性腎症\r\n＃クリオグロブリン血症＝膜性/膜性増殖性糸球体腎炎＋血栓\r\n＃ HCV関連クリオグロブリン血症： C4減少\r\n＃原因不明の末梢チアノーゼ：クリオグロブリン血症、寒冷凝集素症', '＃貧血、血小板減少、補体、特に C4 の低下を生じる。リウマチ因子はほぼ全例で陽性になる。赤沈は亢進、CRP は増加する', '＃C 型肝炎に合併していた場合にはインターフェロンとリバビリンによる治療に効果が期待できる\r\n＃関節リウマチ症状に対して、抗リウマチ薬が用いられる\r\n＃主要臓器症状が合併したときにはステロイド投与の適応になる\r\n＃重篤な患者さんに対して血漿交換が施行される', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、浮腫、眼瞼腫脹、肢腫脹、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、貧血、胸水、 胸腹水、腹水、体液貯留、関節症状、関節痛、関節腫脹、関節炎、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、ネフローゼ、汎血球減少、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、LD.{0,22}上昇、LD.{0,22}増加、LD.{0,22}増多、LD.{0,22}高値、補体.{0,22}低下、補体.{0,22}減少、低補体、血小板.{0,22}増多、血小板.{0,22}上昇、血小板数.{0,22}上昇、血小板数.{0,22}増多、血小板.{0,22}増加、血小板数.{0,22}', '', 0, 0, 0.0, NULL, NULL, 0, 1000),
(31, 'コレステロール塞栓', 30, '', '', '', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、高血圧、血圧高値、腹痛、腹部痛、心窩部痛、季肋部痛、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、蛋白尿、尿蛋白陽性、タンパク尿、尿タンパク陽性、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、胸痛、背部痛、胸部違和感、補体.{0,22}低下、補体.{0,22}減少、低補体、消化管出血、吐血、下血、吐下血、血便、タール便、乏尿、無尿、喘鳴、wheezing、ALP.{0,22}上昇、ALP.{0,22}増加、ALP.{0,22}増多、ALP.{0,22}高値、胆道系酵素上昇.{0,22}上昇、胆道系酵素上昇.{0,22}増加、胆道系酵素上昇.{0,22}増多、胆道系酵素上昇.{0,22}高値、脂肪織濃度上昇、脂肪織炎、脂肪織混濁、皮疹、紅斑', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHUHlnYllaWTJ1bmM" target="_blank">コレステロール塞栓による小腸塞栓</a>\r\n<a href="https://drive.google.com/open?id=1xx8OT-CkN86pYkWL9vDq4gh35sd0fhuN" target="_blank">大動脈壁在血栓によるコレステロール塞栓症</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(32, '成人Still病', 31, '＃成人Still病：大項目39度以上の発熱が1週間以上、2週間以上の関節痛、皮疹、WBC＞10000(好中球＞80％)\r\n　　小項目咽頭痛、リンパ節腫脹または脾腫、AST,ALT,LDH上昇、RF/ANA陰性\r\n大項目2個以上、合計５個以上で診断\r\n除外診断：敗血症、リンパ腫、膠原病(動脈炎など)\r\n＃Still病の皮疹：隆起がないあるいはわずか、数mmの紅色、桃色紅斑\r\n　　発熱時出現、解熱時消失\r\n　　全身性、びまん性ではない、体幹と四肢近位部に多い\r\n　　掻痒感なし、ケブネル現象陽性\r\n＃単峰性/2 峰性の弛張熱、自然消退する皮疹、関節炎を主徴とする全身性炎症性疾患\r\n＃抗核抗体およびリウマチ因子は原則として陰性。マクロファージの活性化やサイトカイン産生が疾患の本態。重症例では DICや血球貪食症候群を生じうる\r\n＃発熱は長期間続く。ときに 39°Cに達する不明熱として来院する。悪寒を伴うが消耗はない\r\n＃サーモンピンク色の皮疹が胸/腹を中心に出現する。皮疹は発熱時にのみ出現し、その後消退する\r\n＃膝、手首、踵を中心に関節痛を生じる。肘/肩痛を生じることもある。肉眼的な異常を伴わない咽頭痛を生じる\r\n＃さまざまな程度のリンパ節腫大、脾臓腫大を生じる。胸膜炎や心膜炎を生じることもある\r\n＃敗血症をはじめとする感染症、自己免疫疾患、発熱を生じる悪性腫瘍が鑑別診断に挙げられる。特に感染性心内膜炎、悪性リンパ腫必ず除外診断する必要がある\r\n＃成人発症Still病の増悪の2つのパタン：マグロファージ活性化型と関節炎型\r\n＃マグロファージ活性化型：血球貪食症候群、フェリチン↑↑、IFN-γ↑、尿中β2MG↑、sIL-2↑、肝障害、血小板減少\r\n＃関節炎型：IL6↑、TNF-α↑、関節痛、血小板増多\r\n＃マグロファージ活性化症候群：成人Still病、SLE、若年性特発性関節炎：過剰炎症、サイトカイン血症\r\n＃重症マグロファージ活性化症候群の治療：パルス延長＋シクロスポリン持続静注\r\n＃皮疹の分類：紅斑、紅斑丘疹、紫斑(点状出血)、水疱', '＃白血球は数万台に著増しうる。貧血を生じる。反応性の血小板増多を生じる\r\n＃汎血球減少を生じていた場合には血球貪食症候群の合併に注意する必要がある\r\n＃血清フェリチンの著増を見る。通常 3000 ng/ml 以上になる\r\n＃GOT、GPT、LDH の増加を認める\r\n＃リウマチ因子/抗核抗体は陰性\r\n＃赤沈亢進/CRP高値、補体/ガンマグロブリンの増加をみる', '＃軽症例では 10-20 mg/日程度のプレドニゾロン内服に NSAIDs を併用する\r\n＃難治例に対してメトトレキセートの併用、TNF 阻害剤やガンマグロブリンの併用が行われる。', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、頭痛、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、関節症状、関節痛、関節腫脹、関節炎、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、肝脾腫、肝腫大、脾腫、筋肉痛、筋.{0,22}痛、SpO2低下、低酸素、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、汎血球減少、咽頭痛、LD.{0,22}上昇、LD.{0,22}増加、LD.{0,22}増多、LD.{0,22}高値、血小板.{0,22}増多、血小板.{0,22}上昇、血小板数.{0,22}上昇、血小板数.{0,22}増多、血小板.{0,22}増加、血小板数.{0,22}', '<a href="https://drive.google.com/file/d/0B0XnmO-afdkHSUM0cnp1Rkl2RHc/view" target="_blank">成人Still病の分類</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHZnRJeDZHZVE2a0E" target="_blank">非典型皮疹を呈した成人Still病の一例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000);
INSERT INTO `zft_program` (`p_id`, `p_title`, `p_sort`, `p_tedian`, `p_jiancha`, `p_zhiliao`, `p_zz`, `p_link`, `p_att_id`, `p_score_type`, `p_age`, `p_sex`, `p_i_title`, `cas_pidsum`, `cas_sum`) VALUES
(33, 'IgG4関連疾患', 32, '＃IgG4関連下垂体炎：複視、口渇、多尿\r\n＃全身器官腫脹、後腹膜線維化、γ-グロブリン高い時、IgG4関連疾患の可能性が高い\r\n＃IgG4病：胸腹部大動脈瘤と関連\r\n＃CRPが正常か低値、CRP高値の時はIgG4関連疾患やHESなどは考えにくい。', '', '', '炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、胸水、 胸腹水、腹水、体液貯留、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、食欲不振、食思不振、食欲低下、食思低下、咳嗽、咳、体重減少、体重低下、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、補体.{0,22}低下、補体.{0,22}減少、低補体、多尿、尿量増加', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHWGNPTUpzbTBzWWc" target="_blank">IgG4-related vasculitisの一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHZFBJNC1MdmZ0Sm8" target="_blank">凝固異常を合併したIgG4関連疾患の一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHSHNHRTNEWUdUdXM" target="_blank">IgG4関連下垂体炎の一例</a>\r\n<a href="https://drive.google.com/open?id=1cWt9gZEVzraTZWraViL1PFGKzfrYEHh6" target="_blank">胆管がんと鑑別困難のIgG4関連胆管炎の1例</a>\r\n<a href="https://drive.google.com/open?id=17EwOHWycgwO8-PxbOiriKLbvqNmYXJd3" target="_blank">好酸球増多と心嚢液貯留を呈したIgG4関連疾患</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(34, '血球貪食症候群', 33, '＃フェリチン＜500の時は、ほぼHPSを否定できる\r\n＃フェリチン＞10000の時は、ほぼ診断できる。成人Still病もありえる。\r\n＃ADA、尿中β2ミクログロブリンも上昇\r\n＃IVLを鑑別する必要がある\r\n＃ウイルス抗体：HSV、CMV IgM,IgG、EBV VCA IgG,IgM、EBV EA IgG、EBNA\r\n＃尿中β2ミクログロブリンと血清IFNγが相関する\r\n＃尿中NAG正常と尿中β2ミクログロブリン上昇は、腎障害とは考えにくく、血球貪食症候群などサイトカインストームの存在を示唆する\r\n＃尿中β2ミクログロブリンは病初期より上昇し早期診断に有用\r\n＃特徴：高サイトカイン血症＋組織障害\r\n＃高サイトカイン血症：フェリチン高値(数千以上)、sIL-2高値(数千以上)、尿中β2ミクログロブリン(数千以上)\r\n＃組織障害：LDH高値(ALTの10倍)', '', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、頭痛、貧血、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、関節症状、関節痛、関節腫脹、関節炎、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、リンパ節.{0,22}腫大、リンパ節.{0,22}腫脹、低血圧、ショック、血圧低下、食欲不振、食思不振、食欲低下、食思低下、肝脾腫、肝腫大、脾腫、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、汎血球減少、咽頭痛、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、LD.{0,22}上昇、LD.{0,22}増加、LD.{0,22}増多、LD.{0,22}高値、CK.{0,22}上昇、CK.{0,22}増加、CK.{0,22}増多、CK.{0,22}高値、CPK.{0,22}上昇、CP', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHbE1OWjZxQ3lSWUk" target="_blank">血球貪食症候群を呈したSFTSVによる重症熱性血小板減少症候群の一例</a>\r\n<a href="https://drive.google.com/file/d/0B0XnmO-afdkHS08yM04wT3hDUHc/view" target="_blank">ウイルス関連血球貪食性リンパ組織球症の一例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(35, '家族性地中海熱', 34, '＃周期性の発熱、腹痛、炎症値上昇、普段は元気、コルヒチンが効く\r\n＃繰り返す高熱と体動困難の漿膜炎発作、2～3日以内緩解。\r\n＃炎症性腸疾患と共有する部分が多く、大腸粘膜の血管透過度低下や発赤、浮腫を呈する場合がある。\r\n＃月経が発作の誘因となることがある。これは月経期にエストロゲン低下、IL-6増加が起因する。', '', '＃治療はコルヒチン内服\r\n＃できれば3か月～6か月以上連日内服後に効果判定\r\n＃コルヒチンが効く疾患：家族性地中海熱、痛風・偽痛風、Bechet病の皮膚粘膜症状、心膜炎\r\n＃コルヒチン使用法は共通である：腹痛・下痢が不安あれば0.5mg＊1錠分1か分2、便秘ある時0.5mg＊2錠分2か分4', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、頭痛、腹痛、腹部痛、心窩部痛、季肋部痛、関節症状、関節痛、関節腫脹、関節炎、下痢、水様便、筋肉痛、筋.{0,22}痛、胸痛、背部痛、胸部違和感、咽頭痛、白血球.{0,22}減少、白血球数.{0,22}減少、白血球.{0,22}軽度減少、頚部痛、単球.{0,22}増加、単球.{0,22}増多、単核球.{0,22}上昇、単核球.{0,22}増多、単核球.{0,22}増加、単球.{0,22}上昇、口内炎、口腔内潰瘍、口内潰瘍', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHTDFaYW1xbEpxZjg" target="_blank">腸間膜脂肪識濃度上昇を呈した家族性地中海熱の一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHc1RsLVpoQjhuRTA" target="_blank">家族性地中海熱の消化管変化</a>\r\n<a href="https://drive.google.com/open?id=15lmPlh7saMzJVbw1xWU9lxIgITkngwmw" target="_blank">家族性地中海熱関連腸炎の1例</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(36, 'PMR', 35, '＃たまに筋内膿瘍をつくる\r\n＃発症時のCK値は低下し、治療後に正常化する。原因は筋痛による活動量低下である。\r\n＃両側対称性の近位筋の痛みやこわばり、把握痛、全身倦怠感や発熱を生じる\r\n＃髪の毛をとかす、コートを着る、椅子から立ち上がるといった動作が困難になる\r\n＃60 歳以上で発症。50 歳以下はまれ\r\n＃15% に側頭動脈炎を合併する\r\n＃側頭動脈炎、感染性心内膜炎、PM/DM、多発性骨髄腫なども類似した症状を生じうる\r\n＃血沈(赤沈)＞100：結核、悪性腫瘍、PMR(TA)、多発性骨髄腫、骨髄炎、心内膜炎、亜急性甲状腺炎、偽痛風', '＃白血球増多はあっても軽微\r\n＃筋肉痛を生じるものの、CPK 上昇は軽微か正常\r\n滑膜炎を反映するMMP3 が陽性\r\nリウマチに感度?特異度が高い抗 CCP抗体が陰性', '＃15mg 程度の少量のプレドニゾロン内服が著効する。効果はたいていの場合 24-48 時間以内に認められる\r\n＃15mgより開始、3週間ごと2.5mgづつ減量、10mgを3週間飲み終わった後、4～8週ごと1mgづつ減量\r\n＃ステロイドによる改善が認められないのなら、本症以外の疾患を見ている可能性を考慮する', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、倦怠感、脱力、体調不良、浮腫、眼瞼腫脹、肢腫脹、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、頭痛、貧血、関節症状、関節痛、関節腫脹、関節炎、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、体重減少、体重低下、筋肉痛、筋.{0,22}痛、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、動物.{0,30}接触、犬、猫、豚、ヤギ、鶏、ネズミ、鼠、市場、野外.{0,30}活動、農業、河、川、咬傷、農家、農作業、胸痛、背部痛、胸部違和感、腰痛、末梢神経障害、しびれ、痺れ、異常知覚、感覚異常、感覚鈍麻、知覚障害、sil.{0,22}上昇、sil.{0,22}増加、sil.{0,22}増多、sil.{0,22}高値、血痰、頚部痛、嗄声、かすれ、ALP.{0,22}上昇、ALP.{0,', '', 0, 0, 0.0, NULL, 'PET所見', 0, 1000),
(37, '脊椎関節炎', 36, '＃強直性脊椎炎：\r\n　　初期症状：背部痛＋末梢の関節炎\r\n　　晩期症状：両肺尖部嚢胞性病変＋馬尾症状\r\n　　HLA-B27陽性の 炎症性腸疾患→必ず強直性脊椎炎を発症\r\n　　SAPHO：前胸部痛＋手足の水泡、骨化過剰症\r\n＃軀幹脊椎関節炎：四肢脊椎関節炎や乾癬と類縁\r\n＃乾癬性関節炎：頸部回旋時痛、踵の痛み、指趾の腫脹は乾癬性関節炎による脊椎炎、付着部炎、指趾炎の可能性ある。治療に反応しない乾癬、皮膚脱色素は皮膚T細胞リンパ腫の可能性\r\n＃体幹、四肢に角化性紅斑と掌蹠の膿疱：尋常性膿疱性乾癬、反応性関節炎\r\n＃炎症性腰痛(脊椎炎、仙腸関節炎)：40歳以下で緩徐に発症する背部と臀部痛、夜間痛、安静時痛、運動すると寛解する', '', '', '発熱、高熱、不明熱、微熱、悪寒、弛張熱、炎症.{0,22}高値、CRP.{0,22}高値、CRP.{0,22}上昇、炎症.{0,22}上昇、炎症.{0,22}亢進、高度{0,22}炎症、高CRP、関節症状、関節痛、関節腫脹、関節炎、下痢、水様便、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、筋肉痛、筋.{0,22}痛、胸痛、背部痛、胸部違和感、腰痛、フェリチン.{0,22}上昇、フェリチン.{0,22}増加、フェリチン.{0,22}増多、フェリチン.{0,22}高値、便秘、消化管出血、吐血、下血、吐下血、血便、タール便、頚部痛、脂肪織濃度上昇、脂肪織炎、脂肪織混濁、皮疹、紅斑、紫斑、湿疹、丘疹、発疹、皮膚病変、皮膚障害、嚥下障害、嚥下困難', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHM0xRekVLUENBVDA" target="_blank">軀幹脊椎関節炎</a>\r\n<a href="https://drive.google.com/open?id=1eDBhrEBeL5cnzEyEfOGZUEUs6yoJxhIX" target="_blank">脊椎関節炎review</a>', 0, 0, 0.0, NULL, NULL, 0, 1000),
(38, 'Heyde症候群', 37, '＃Heyde症候群(type 2A von Willebrand)：大動脈弁狭窄の時(圧較差＞50)に高いずり応力→VWF高分子多量体が減少→消化管など粘膜に血管異形成→出血\r\n＃消化管血管異形成の患者の32％がASを合併する\r\n＃大動脈弁置換術後95％の患者で症状が消失', '＃検査：フォン・ウィルブランド因子マルチマー解析→高分子多量体/マルチマーが減少', '', '高血圧、血圧高値、呼吸困難、呼吸苦、息切れ、息苦、起座呼吸、貧血、胸水、 胸腹水、腹水、体液貯留、血小板.{0,22}減少、血小板数.{0,22}減少、血小板.{0,22}低下、胸痛、背部痛、胸部違和感、消化管出血、吐血、下血、吐下血、血便、タール便', '', 0, 0, 0.0, NULL, NULL, 0, 1000),
(39, 'ポルフィリン症', 38, '＃繰り返す原因不明の腹痛\r\n＃四肢脱力、筋肉痛、不安などの精神神経症状\r\n＃褐色尿\r\n＃けいれん＋低Na血症\r\n＃親族に同じような症状を経験した人', '', '', '貧血、腹痛、腹部痛、心窩部痛、季肋部痛、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、肝機能.{0,22}障害、肝機能.{0,22}異常、肝障害、黄疸、黄染、肝機能悪化、肝萎縮、肝硬変、関節症状、関節痛、関節腫脹、関節炎、四肢.{0,22}痛、下肢.{0,22}痛、上肢.{0,22}痛、大腿.{0,22}痛、下腿.{0,22}痛、けいれん、痙攣、体重減少、体重低下、肝脾腫、肝腫大、脾腫、筋肉痛、筋.{0,22}痛、視力障害、視力低下、視野異常、視野障害、複視、視野狭窄、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム.{0,22}低、血清ナトリウム.{0,22}低、多飲、膨満感、腹部違和感、心窩部不快感、末梢神経障害、しびれ、痺れ、異常知覚、感覚異常、感覚鈍麻、知覚障害、便秘、羞明、光過敏、口内炎、口腔内潰瘍、口内潰瘍', '', 0, 0, 0.0, NULL, NULL, 0, 1000),
(40, '副腎不全', 39, '＃低Na血症(グルココルチコイド欠損)と高K血症(ミネラルコルチコイド欠損)が同時に出現する時は下垂体ではなく原発性副腎不全である。\r\n＃10～20mg/日プレドニゾロンを3週間以上内服→副腎抑制\r\n＃収縮圧＞100mmHg→アジソン病を除外できる？？？\r\n＃副腎不全の時：Naが細胞内に移動する→低Na血症\r\n＃副腎不全→性腺機能低下→眉、腋窩毛、陰毛減少\r\n＃副腎の相対的不足(ACTH,COR低下なし、むしろ上昇)：低Na血症、低蛋白血症、貧血→プレドニン5㎎＊Bid\r\n＃原因不明のカテコラミン不応性低血圧：副腎不全\r\n＃コルチゾール正常範囲、ACTH高値、日中変動なしの疾患：ACTH増多のCushing、原発性副腎不全(ストレス時はコルチゾール増多するはず)\r\n＃貧血、MDS→凝固異常、血小板機能異常→副腎梗塞・出血→腹痛\r\n＃手足の爪が紫色：副腎不全による色素沈着も考えられる\r\n＃副腎出血：抗凝固治療中の患者で急激な両側側腹痛→両側副腎出血を疑う必要がある。両側副腎出血に伴う副腎不全はほとんど永続性である\r\n＃早朝のコルチゾール＜4～5なら恐らく副腎不全。15～17以上なら否定的。\r\n＃早朝コルチゾール5～15や随時測定＜4なら診断も除外もできない。', '＃低血糖、低Na、高K、高Ca血症を生じる、好酸球数の増加を認める。\r\n＃患者さんの6割に赤沈亢進を見る\r\n＃ACTH、コルチゾールの採血で診断できる。任意のタイミングでの採血検査であっても、ACTHの上昇とコルチゾールの低下、あるいは増加不良が確認できれば可能性は高い。\r\n＃TSHが高値となっていることが多い\r\n＃蓄尿を行い、尿中17-OHCS、尿中17-KS、尿中コルチゾールの低値を証明する\r\n＃Rapid ACTHテスト：ACTH製剤(コートロジン)0.25mgを静注し、コルチゾール測定（前と30・60分）。正常者は、負荷後30分値または60分値が前値の2～4倍に増量する。(前値が正常であることを確認)クッシング症候群のうち腺腫では無反応または増加のいずれも認められ、過形成では反応陽性であり、アジソン病や皮質ステロイド投与による医原性副腎不全では増加反応を示さない。', '＃ステロイド補充療法：低用量ヒドロコルチゾン朝10mg、午後(16時)5mg；高用量朝20mg、午後10mg\r\n＃ヒドロコルチゾンの半減期が短いため、ACTHが適切なヒドロコルチゾン補充量の指標にはならない。検査データは指標にならないため、適切補充量は臨床的(自覚症状)に判断し、最小量に使う\r\n＃ステロイド減量protocol：ヒドロコルチゾン朝20mg、午後(16時)10mg開始する→1か月後、自覚症状なければ午後5mg→1か月後、自覚症状なければ午後0mgへ→1か月後、自覚症状なければ午前15mg(或いはプレドニン3mgまで減量)→以降漸減不要、朝服薬前にコルチゾール測定し、10以上であれば、ヒドロコルチゾン中止可\r\n#副腎不全患者の感染症合併：37度の時グルココルチコイド1.5倍；38度　の時グルココルチコイド2倍\r\n', '腎機能.{0,22}障害、腎機能.{0,22}低下、腎不全、腎機能.{0,22}悪化、腎機能.{0,22}増悪、腎障害、腎機能.{0,22}異常、倦怠感、脱力、体調不良、意識変容、意識障害、意識レベル低下、意識混濁、意識朦朧、意識もうろう、興奮、不穏、傾眠、せん妄、昏迷、失見当識、嘔気、嘔吐、吐き気、悪心、吐気、気分不良、低血圧、ショック、血圧低下、食欲不振、食思不振、食欲低下、食思低下、下痢、水様便、体重減少、体重低下、好酸球.{0,22}増多、好酸球.{0,22}増加、好酸球数.{0,22}増多、好酸球.{0,22}上昇、好酸球.{0,22}著増、低Na、低ナトリウム、血中Na.{0,22}低、血清Na.{0,22}低、血中ナトリウム.{0,22}低、血清ナトリウム.{0,22}低、胸痛、背部痛、胸部違和感、抗核抗体.{0,22}陽性、抗核抗体.{0,22}上昇、抗核抗体.{0,22}強陽性、抗核抗体.{0,22}高値、多飲、多尿、尿量増加、', '<a href="https://drive.google.com/open?id=0B0XnmO-afdkHcDhpV0s0Y3RuN0U" target="_blank">下垂体卒中による副腎不全の一例</a>\r\n<a href="https://drive.google.com/open?id=0B0XnmO-afdkHVUplSWVUd0tmc0k" target="_blank">尿崩